@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendship-bracelets.net") {
body {
overflow-x:hidden !important;
background-color:#212121 !important;
background: -moz-linear-gradient(top,  rgba(12,50,63,1) 0%, rgba(12,50,63,0.99) 1%, rgba(125,185,232,0) 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(12,50,63,1)), color-stop(1%,rgba(12,50,63,0.99)), color-stop(100%,rgba(125,185,232,0)));
background: -webkit-linear-gradient(top,  rgba(12,50,63,1) 0%,rgba(12,50,63,0.99) 1%,rgba(125,185,232,0) 100%);
background: -o-linear-gradient(top,  rgba(12,50,63,1) 0%,rgba(12,50,63,0.99) 1%,rgba(125,185,232,0) 100%);
background: -ms-linear-gradient(top,  rgba(12,50,63,1) 0%,rgba(12,50,63,0.99) 1%,rgba(125,185,232,0) 100%);
background: linear-gradient(top,  rgba(12,50,63,1) 0%,rgba(12,50,63,0.99) 1%,rgba(125,185,232,0) 100%);}
#footer a {color:#bbb !important;}
#footer a {color:#fff !important;}
th {background-color:#ccc !important;}
#logotype, #logo, .leaderboard_ad_td1, .leaderboard_ad_td2 {display:none;}
#header a[href="donate.php"], #header a[href="http://www.facebook.com/pages/Friendship-Braceletsnet/137456386333320"] {display:none;}
#header {background:none !important; border:none !important;}
#header {position:absolute; top:25px; left:-10px;}
table tr td, .invisible_table table td {background-color:#000;}
a:link, #content .pageSelect a {color:#334957 !important;
-webkit-transition: all 200ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
   -moz-transition: all 200ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
    -ms-transition: all 200ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
     -o-transition: all 200ms cubic-bezier(0.420, 0.000, 0.580, 1.000); 
        transition: all 200ms cubic-bezier(0.420, 0.000, 0.580, 1.000);}
a:hover {color:#999999 !important; underline:none !important;}
#navigation a:link, #navigation a:visited {background-color:#eee !important;}
#navigation a.here:link, #navigation a.here:visited, #navigation a.here:hover, #navigation a.here:active {background-color:#fff !important;}
#wrapper {position:relative; left:85px;}

.gallery_table tr td {background-color:#eee !important;}
#content .pageSelect li .inactive, #content .pageSelect li .inactive:hover {background-color:#999 !important;}
#content .pageSelect a:hover {border-color:#000 !important; color:#000 !important; background-color:#fff !important;}
#content .pageSelect li a {background-color:#eee !important; border-color:#334957 !important;}
html body div#wrapper div#main div#content table.generator_choice_table tbody tr td {background-color:#eee !important;}
.agen_mode_td, .agen_resize_td, .agen_color_td, .agen_main, .agenacode_tr, .agen_code_td, .agen_debug {background-color:#eee !important;}
.newsfeed td, #login_table td, .tutorials_table td, .guestbook_table td, .guestbook_table_newentry td, #patterns_table td, .invisible_table tbody tr td p table tbody tr td, .forum_table td {background-color:#eee !important;}
font.weak {color:#8C8C8C !important;}
img[src="im/misc/white.jpg"] {display:none;}

}
