css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.space.com") {


        /* make annoing things hidden*/
        #popup-div {display: none !important;
                      
            }     
        
        #top_side {display: none !important;
                      
            }        
                
        #bg_underlay {display: none !important;
                      
            }        
        #ad-leaderboard {display: none !important;
                      
            }
        #ad_billboard  {display: none !important;
                      
            } 
        .image_gall {display: none !important;
                      
            }
        .site_options {display: none !important;
                      
            }
         #likebar_fixed_center  {display: none !important;
                      
            } 
         #picad_div  {display: none !important;
                      
            }
         .center_adsense, .about_adsense, .section_badsense  {display: none !important;
                      
            }
            
         .rn_adsense, .sr_adsense, .vsense_goo, .vidsense, .vadsense {display: none !important;
                      
            }
            
         .poll_box, .leaderboard {display: none !important;
                      
            }  
         
                
        .rnav_spacer {
            display: none !important;
        }

        .main_content iframe, .sr_comp {
            display: none !important;
        }

        .search_cont_top {
         background-image: none !important;  
        }
        
        #comments_cont {
            display: none !important;
        }
        
        /* make the logo smaller */
        
        #header {
                height: 85px !important;
                margin-top: -10px !important;
                
                }

        .search_cont_top {
                    height: 44px !important;
                    }

        
        
        /* change the style of the main divs*/
        
        body {  color: #111111 !important;
                background-color: #FFF !important;          
                background: none !important;
                background-image: url("https://ssl.gstatic.com/android/market_images/web/background_stripes.gif") !important;

                
        }
        #underlay { background-color: #e0e0e0 !important;
                   border-style:solid !important;
                   border-width:2px !important;
                   border-color: grey !important;
                   box-shadow: 0 5px 5px rgba(0, 0, 0, 0.5);

            }
       
       * a:hover {  
            text-decoration: underline !important;
            color:#F0A49D !important; 
                
        }
       
       
       * a:active {  
            color:#64ABFC !important; 
                
        }
       
       
      /* change the navigation menu */ 
       #navigation {  
                background: grey !important;
                border: 1px solid #e0e0e0 !important;
                border-radius: 3px !important;
                box-shadow: 0 0 3px rgba(0, 0, 0, 0.5);
                
        }
       
       #navigation span {  
                background-color: #FFF !important;          
                background: none !important;
        }
       
       #navigation span a{  
                background-color: black !important;          
                background: none !important;
        }
       
       #navigation span span a{  
                padding: 7px 12px !important;
                
                
        }
       
              
       #navigation span span:first-child a {  
               border-left: 0px !important;
                
        }
       
       #navigation span span:last-child a {  
               border-right: 0px !important;
                
        }
       
       a.section_topics {  
               display: none !important;
               
            
        }
       
       
       
       
       .tstories a { color: #111111 !important;          
                     font-size: 12px !important;   
        }
        .prem_left { 
                   border-style:ridge !important;
                   border-width:0px !important;
                   border-color: grey !important;

            }
        
        img      { opacity: 0.9 !important;
                        }
        
        
        
/* making more transparent the see more buttons */  
        
        a>img:first-child { 
                opacity: 0.75 !important;
                              
                        }
        
        
        
/*adjutsting the borders of all the images */
         .img_overlay { border: 3px solid #111111 !important;
                        border-radius: 3px !important;
                        box-shadow: 0 0 1px #000000 !important;
                        display: block !important;
                        opacity: 0.5 !important;
                        position: relative !important;
                        
                        z-index: 1 !important;
                        }
        .overlay_iFF {  height: 235px !important;
                        margin-bottom: -240px !important;
                        width: 355px !important;
                        }
        
        .overlay_i192 {
                       height: 123px !important;
                       margin-bottom: -128px !important;
                       width: 187px !important;
                      }

        .overlay_i00 {
                      height: 62px !important;
                      margin-bottom: -67px;
                      width: 95px !important;
                        }

        .overlay_i07 {
                      height: 88px !important;
                      margin-bottom: -93px;
                      width: 135px !important;
                      }


        .overlay_i235 {
                    height: 153px !important;
                    margin-bottom: -156px;
                    width: 232px !important;
}


        .overlay_i107 {
                    height: 70px !important;
                    margin-bottom: -73px;
                    width: 104px !important;
}


        .overlay_i120 {
                    height: 76px !important;
                    width: 116px !important;
}


        .overlay_i01 {
                    height: 105px !important;
                    width: 158px !important;
}


/* adjusting the nav bar menu*/        
        #side_nav   {
                        
                         border: 0 !important;
                         background-color: #FFF !important;
                         background: none !important;
                         height: 620px !important;
                         margin-bottom: 10px !important;
                    }
        
/* adjusting the slideshow menu bar */

        .prem_nums {
                    
                    border: 1px solid #e0e0e0 !important;
                    border-radius: 5px !important;
                    box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5) !important;
                    padding: 0 2px !important;
                    border-top: 1px solid grey !important;
                    width: 99.5% !important;
                    
            }        

         
        .prem_nums>div:nth-child(odd) {
                    display: none !important;
                                        
            } 

        .featured_button {
                background-image: none !important;
                background-color: grey !important;
                color: white !important;
                border: 1px solid #e0e0e0 !important;
                border-radius: 3px !important;
                box-shadow: 0 0 3px rgba(0, 0, 0, 0.5)!important;
                padding: 8px 17px !important;
                margin: 2px 1px !important;
                
                      
            }        

            .sitel_border {
                    margin-top: -7px !important;
                    padding: 0px !important;
                    

                            }





/* adjusting the left side video section*/        
        .side_row   {   background-color: #FFF !important;          
                        background: none !important;
                        border-style:solid !important;
                        border-width:1px !important;
                        border-color: grey !important;
                        box-shadow: 0 3px 2px rgba(0, 0, 0, 0.5);
                        border-radius:3px;
                        height: 565px !important;
                        overflow: hidden !important;
                        margin-bottom: 50px !important;
                        }
        .rn_vplayer {
                         border: 0 !important;
                        
                    }


        
        
        
        .rn_vptop   {   background-color: #FFF !important;          
                        background: none !important;
                        border-bottom: 1px solid black !important;
                        
                        
                        }
        .rn_vpbottom {  background-color: #FFF !important;          
                        background: none !important;
                        height: 320px !important;
                        
                        }
        .rn_vplcont {   background-color: #FFF !important;          
                        background: none !important;
                        
                        }
                        
        .rn_intro a  { color: #111111 !important;          
                              }
        
        .rn_intro a:hover  { 
                        text-decoration: none !important;
                              }
        
        .rn_pretitle a  { color: #111111 !important;          
                        font-size: 16px !important;
        }
        
        
        .rn_pretitle a:visited  { color: #1984FF !important;          
                                }
        
        .side_row:first-child {display: none !important;
                      
            }
        .side_row:nth-child(n+3) {display: none !important;
                      
            }   
  
  
/* adjusting  the headlines section */ 
        .section_headlines { 
            color: #111111 !important;
            padding-top: 14px !important;
        
        
                        
        }
        

 
 
 /* adjusting the last section the one with the hot topics */
 
        .hhot_topics {
            color: #111111 !important;
            background-color: #FFF !important;          
            background: none !important;
            border-style:solid !important;
            border-width:1px !important;
            border-color: grey !important;
            box-shadow: 0 -2px 2px rgba(0, 0, 0, 0.5);
            border-radius:3px;
            float: right !important;
            padding: 15px !important;
            width: 305px !important;
            height: 670px !important;
            overflow: hidden !important;
            margin-top: -705px !important;
            margin-right: -342px !important;
            
}


        .hh_header {
            padding-bottom: 15px !important;
            
}       

        .hh_main  {
            padding-bottom: 5px !important;
            height: 270px;
            border-bottom: 1px solid gray;
            
            
}       
        
        
        
        .hh_maintext {
            padding: 15px 10px 0 10px !important;
            text-align: justify !important;
            width: 90% !important;
            
}

        .hh_maintext div{
            padding-bottom: 0 !important;
            
}


        .hh_sub {
            padding: 10px !important;
            width: 300px !important;
            height: 345px !important;
            overflow: auto;
            margin-left: -10px !important;
            margin-top: 5px !important;
                        
}
        
        
        .hh_subconts {
            font-size: 11px;
            width: 110px !important;
            padding: 10px !important;
            margin-right: 10px !important;
}

        
        .hh_maintext a, .hh_subconts a {
            color: #111111 !important;
}

        



 /* adjusting the footer */
 
        .footer, .site_pages, .media_network, .footer_mailing, .footer_share {
              background-color: #c8c8c8 !important;
              background-image: none !important;
                }
        
        
        .footer a, .footer div {
            color: #111111 !important;
            font-size: 11px !important;
                }
                
 
        .footer  {
            border: 1px solid #e0e0e0;
            border-radius: 15px;
                }
        
 
        .footer a {
            font-size: 9.5px !important;
                }
 
        .footer_mailing  {
            margin-top: 20px;
            }
        
        
        .footer_share  {
            margin-top: 35px;
            }
 
 
 
 
 /* adjusting the articles section */
 
       .rls_cont, .rls_subcont {
        width: 950px !important;
}

        .rls_rspace, .rls_row {
    padding: 0 30px;
    margin-left: 60px !important;
    
}


      
        .album_overall {
    width: 930px !important;
    padding: 20px !important;
    border-radius: 5px !important;
    margin-bottom: 10px !important;
    border-bottom: 1px solid #5B6857 !important;
    
}
        
        .album_cont {
    width: 900px !important;
    height: 70px !important;
    padding: 15px !important;
    border-radius: 5px !important;
    opacity: 0.6 !important;
    
    
}
        
        
        
        .album_cont span {
    display: none !important;
}
        
        .album_title h1 {
    font-size: 28px !important;
    line-height: 42px !important;
    margin: 0;
    padding: 0;
}


/* fixing the close button from the expanded image section */
        .close {
        z-index: 100 !important;
}


        
        
        
        .aa_share, .bot_aa_share  {
            display: none;
    
}

        .about_text  {
    width: 890px !important;
    padding: 20px !important;
    border-radius: 5px !important;
    
}
        
        .quiz_cont {
    margin-right: 15px !important;
    
}
 
 
        .asset_section_links li {
    margin-left: 15px !important;
    padding: 0 40px !important;
       
}


 
      
        .album_bottom {
    display: none !important;
    
}
 
 
 /* fixing the videos section */
 
    #facebook_cont, #twiter_cont, #g_plusone_cont {
        background-color: #717070 !important;
        
} 
 
    .rnav_spacer {
        display: none !important;
        
} 
    
    .video_header {
       
        background-color: #717070 !important;
        background-image: none !important;
        border-radius: 4px !important;
        margin-bottom: -1px !important;
        
}
  
    #myExperience {
        margin-bottom: -1px !important;
        background-color: transparent !important;
        background-image: none !important;
        opacity: 0.75 !important;
        
}

    div.main_content > div:nth-child(6) {
        background-color: #717070 !important;
        background-image: none !important;
        border-radius: 4px !important;
        margin-bottom: 20px !important;
        padding-top: 7px !important;
        padding-bottom: 0 !important;
        color: white !important;
        
}
  
   #cat_button, div.main_content > div:nth-child(6) a img {
        opacity: 0.75 !important;
        
}
  
  
  #video_description {
        width: 945px !important;
        
}
  
  
  .video_slider {
        background-color: #717070 !important;
        background-image: none !important;
        width: 950px !important;
        border-radius: 4px !important;
        margin-top: -10px !important;
        
}

    #relatedvideos, .vslide_mid {
        width: 840px !important;
        
        
}

    .vslide_left {
        margin-left: 15px !important;
        
        
}
  
  
  
/* adjusting the image-of-a-day section */  
  
        .album_imgprev {
        width: 812px !important;
        opacity: 0.9 !important;
}
  
        .album_imgspace {
        opacity: 0.9 !important;
        
}
  
        .album_imgslide {
        width: 888px !important;
        border-top: 1px solid #7E8A7A !important;
        border-right: 1px solid #4B4F49 !important;
        border-left: 1px solid #4B4F49 !important;
        border-bottom: 1px solid #1E1E1E !important;
        border-radius: 4px !important;
}
        
        #albumul {
        overflow: visible !important;
        width: 600px !important;
        
}
  
  
        .album_pagenum {
        padding-left: 150px !important;
    
}

/* adjust the infographics section */

        .about_title {
          width: 900px !important;
          border-radius: 5px 5px 5px 5px !important;
          height: 40px !important;
          opacity: 0.6 !important;
            padding: 15px !important;
           
}

        .about_cont {
          width: 930px !important;
          
           
}

        .aa_overall {
        border-bottom: 1px solid #5B6857 !important;
        border-radius: 5px 5px 5px 5px !important;
        margin-bottom: 10px !important;
        padding: 20px !important;
        width: 930px !important;
          
           
}

        .about_bottombg {
          display: none !important;
           
}

        .about_text {
          width: 890px !important;
          
           
}

        .about_text div[style="width:100%; float:left;"]{
          width: 20% !important;
          
           
}

        .wp_ind {
         margin-bottom: 20px !important;
         height: 175px !important;
         padding: 0px !important; 
           
}


/* adjust the line that divides the sections content */

        
        .mps_section {
            padding-top: 0 !important;
            
}




/* center the facebook comments section */
        .about_text > div:last-child {
        width: 900px !important;
        text-align: center !important;
        margin-top: 30px !important;        
}

  
  
  
 /* color the text from white to black */
        
        .prem_strs, .iotday, .wp_ind a, .wp_ind > div:last-child{
              color: #111111 !important;
                }
        
        .rn_playvid a {
             color: #111111 !important;
                           }
        .rn_playvid a:hover {
             color: #F0A49D !important;
                           }
        
        .rn_playvid a:visited {
             color: #1984FF !important;
                           }
        
        .rn_vpltext a {
             color: #111111 !important;
                           }
  
        .rn_vpltext a:hover {
             color: #F0A49D !important;
                           }

        .rn_vpltext a:visited {
             color: #1984FF !important;
                           }
        
        .section_h1 { color: #111111 !important;          
                      font-size: 29px !important;   
        }  
        
        
        
        .sh_title_section { color: #111111 !important;          
                         
        }  
        .sh_title a{ color: #111111 !important;          
                         
        }    
        .sh_cont span { color: #111111 !important;          
                        
        }     
        .sh_cont a { color: #111111 !important;          
                        
        }   
     
     
     
     
     
     
}
