css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dvice.com") {


body {
    background: none !important;
    background-color: #E5E5E2 !important;
}

#page {
    background: none !important;
    margin: 0 10% !important;
    width: 80% !important;
    border: 1px solid grey !important;
    box-shadow: 0 5px 5px rgba(0, 0, 0, 0.5) !important;
}
.area-body {
    margin-left: 5% !important;
    margin-right: 2% !important;
}



#header {
    height: 80px !important;
}


#header-primary, #header-secondary, #header-tertiary {
    height: 1em !important;
    margin-bottom: 1.2em !important;
    
}

#header-secondary {
    height: 1em !important;
    margin-bottom: 0.5em !important;
    
}


#logo {
    position: relative !important;
    z-index: 1000 !important;
    
}

#logo img {
    height: 220px !important;
    max-width: 70% !important;
    left: 20px !important;
    top: -20px !important;
}

#header .main-categories-menu .menu {
    font-size: 1.8em !important;
}

.trending-tags {
    font-size: 1.1em !important;
  
}


/*

#content {
    position: relative !important;
    z-index: 1 !important;
}
*/

.area-head, #secondary-content {
    background: none !important;
}

#wedge, #block-dart-dart-tag-300x250-unit, #footer, #secondary-content {
    display: none !important;
}


#block-views-trending-tags-block  {
    height: 25px !important;
    float: right !important;

}

.view-content, .views-masonry-item, .views-row, .views-row-17, .masonry-brick {
    background: none !important;
    width: 100% !important;
    float: left !important;
    position: static !important;
    margin-bottom: 10px !important;
    

}

html.js body.html div#site div#page header#header.slab div.navs div#header-secondary.region div#block-views-trending-tags-block.block div.view div.view-content {
    width: 33% !important;
    float: none !important;
    position: relative !important;
    
   

}

html.js body.html div#site div#page header#header.slab div.navs div#header-secondary.region div#block-views-trending-tags-block.block div.view {
    width: 100% !important;
    float: right !important;
    

}

html.js body.html div#site div#page header#header.slab div.navs div#header-secondary.region div#block-views-trending-tags-block.block h2.block-title {
    display: none !important;
    

}



.node, .node-27756, .node-teaser, .tile, .tile-default, .tile-flipped, .area, .node-content {
    background: none !important;
    width: 100% !important;
    margin: 0 !important;
    position: static !important;
    

}


.tile-flipped, .field-name-field-cover-media {
    float: left !important;
    margin: 0 !important;
    margin-right: 10px !important;
    /*height: 150px !important;
    width: 150px !important;*/

}


.field-name-field-cover-media {
    height: 100% !important;
    max-width: 400px !important;

}




.node-teaser, .node-teaser, .area-footer {
    margin: 0 !important;
    

}


.node-title a {
    color: #42145F !important;
    font-family: Arial,Helvetica,Sans-serif !important;
    font-size: 23px !important;
}


.area {
    border-top: 1px solid #EBEBE9 !important;
    padding-top: 10px !important;
    border-bottom: 0 !important;
    width: 99% !important;
    padding-bottom: 0 !important;
    margin: 0 !important;
}

#primary-content {
    width: 100% !important;
    
}


}
