css:
@-moz-document url-prefix("https://github.com/organizations/paperboy-heteml"), url-prefix("https://github.com/paperboy-heteml") {.github-logo-4x-hover {
	opacity:0;
}

#header {
	background: #008ecd; /* Old browsers */
	background: -moz-linear-gradient(top,  #008ecd 1%, #007cc9 100%); /* FF3.6+ */
	background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#008ecd), color-stop(100%,#007cc9)); /* Chrome,Safari4+ */
	background: -webkit-linear-gradient(top,  #008ecd 1%,#007cc9 100%); /* Chrome10+,Safari5.1+ */
	background: -o-linear-gradient(top,  #008ecd 1%,#007cc9 100%); /* Opera 11.10+ */
	background: -ms-linear-gradient(top,  #008ecd 1%,#007cc9 100%); /* IE10+ */
/*position: fixed;
 * width:100%;*/
}

#header a {
	text-shadow: none;
	color: #313131;
}

.pagehead > ul.tabs {
	background: #008ecd; /* Old browsers */
	background: -moz-linear-gradient(top,  #008ecd 1%, #007cc9 100%); /* FF3.6+ */
	background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#008ecd), color-stop(100%,#007cc9)); /* Chrome,Safari4+ */
	background: -webkit-linear-gradient(top,  #008ecd 1%,#007cc9 100%); /* Chrome10+,Safari5.1+ */
	background: -o-linear-gradient(top,  #008ecd 1%,#007cc9 100%); /* Opera 11.10+ */
	background: -ms-linear-gradient(top,  #008ecd 1%,#007cc9 100%); /* IE10+ */
}

.pagehead > ul.tabs li a {
	text-shadow: none;
	color: #fff;
	border-bottom: 2px solid #005DAF;
}

.pagehead > ul.tabs li a:hover{
	background: #36a7db; /* Old browsers */
	background: -moz-linear-gradient(top,  #36a7db 1%, #008ecd 99%); /* FF3.6+ */
	background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#36a7db), color-stop(99%,#008ecd)); /* Chrome,Safari4+ */
	background: -webkit-linear-gradient(top,  #36a7db 1%,#008ecd 99%); /* Chrome10+,Safari5.1+ */
	background: -o-linear-gradient(top,  #36a7db 1%,#008ecd 99%); /* Opera 11.10+ */
	background: -ms-linear-gradient(top,  #36a7db 1%,#008ecd 99%); /* IE10+ */
	border-bottom: 2px solid #096EC7;
	color: #323232;
}

.pagehead > ul.tabs li a.selected, .pagehead > ul.tabs li a.selected:hover {
	background: #008ecd; /* Old browsers */
	background: -moz-linear-gradient(top,  #008ecd 1%, #36a7db 99%); /* FF3.6+ */
	background: -webkit-gradient(linear, left top, left bottom, color-stop(1%,#008ecd), color-stop(99%,#36a7db)); /* Chrome,Safari4+ */
	background: -webkit-linear-gradient(top,  #008ecd 1%,#36a7db 99%); /* Chrome10+,Safari5.1+ */
	background: -o-linear-gradient(top,  #008ecd 1%,#36a7db 99%); /* Opera 11.10+ */
	background: -ms-linear-gradient(top,  #008ecd 1%,#36a7db 99%); /* IE10+ */

}

.pagehead > ul.tabs li a.selected, .pagehead > ul.tabs li a.selected:hover {
	color:#043040;
	border-bottom: 2px solid #f70f72;
}

}
