css:
@-moz-document url-prefix("https://github.com/organizations/paperboy-calamel"), url-prefix("https://github.com/paperboy-calamel") {/* --- header --- */

.github-logo-4x-hover {
opacity:1;
}

#header {
background: #48be5b;
background: -moz-linear-gradient(#48be5b,#039e41);
background: -webkit-linear-gradient(#48be5b,#039e41);
/*position:fixed;
width:100%;*/
}

#header a {
text-shadow:none;
color:#000;
}

#header a:hover {
color:#F1C841;
}

.github-logo-4x-hover {
opacity: 0;
}

.topsearch {
float: left;
clear: none;
width: auto;
border-left: 1px solid #007830;
box-shadow: -1px 0 0 #005b24;
padding: 10px 10px 10px 10px;
margin: 0 0 0 10px;
position: relative;
}

#user-links .setting_warning, #user-links .unread_count {
position: absolute;
top: -6px;
left: 75%;
float: left;
margin-left: -7px;
background: rgba(243, 177, 0, 0.8);
border-bottom: 1px solid #bf8b00;
border-radius: 2px;
padding: 3px 4px;
line-height: 1;
font-size: 10px;
color: white;
font-weight: bold;
white-space: nowrap;
}


/* --- tab --- */
.pagehead > ul.tabs {
position: relative;
margin: 10px 0 0 0;
font-size: 12px;
font-weight: bold;
background: #9ada18;
background: -moz-linear-gradient(#9ada18,#7cb014);
background: -webkit-linear-gradient(#9ada18,#7cb014);
-ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr='#9ada18',endColorstr='#7cb014')";
border: 1px solid #EAEAEA;
border-bottom-color: #CACACA;
border-radius: 3px;
}

.pagehead > ul.tabs li a {
display: block;
text-align: center;
line-height: 35px;
font-size: 12px;
color: #444;
text-decoration: none;
text-shadow: 0 1px 0 #9ada18;
border-right: 1px solid #EEE;
border-right-color: rgba(0, 0, 0, 0.04);
border-left: 1px solid #FCFCFC;
border-left-color: rgba(255, 255, 255, 0.7);
border-bottom: 2px solid #9ada18;
}

.pagehead > ul.tabs li a:hover {
background: #b6f92d;
background: -moz-linear-gradient(#b6f92d,#96d417);
background: -webkit-linear-gradient(#b6f92d,#96d417);
-ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr='#b6f92d',endColorstr='#96d417')";
border-bottom: 2px solid #F1C841;
color: #000;
}

.pagehead > ul.tabs li a.selected, .pagehead > ul.tabs li a.selected:hover {
color: #000;
background: #b6f92d;
background: -moz-linear-gradient(#b6f92d,#96d417);
background: -webkit-linear-gradient(#b6f92d,#96d417);
-ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr='#b6f92d',endColorstr='#96d417')";
border-bottom: 2px solid #F1C841;
}}
