css:
@-moz-document url-prefix("http://games.on.net/forums/viewtopic.php") {
.bg1 {
  -webkit-border-radius: 4px;
  border-radius: 4px;
  -webkit-box-shadow:  0px 0px 3px 1px #CCC;
  box-shadow:  0px 0px 3px 1px #CCC;
  margin-top: 6px;
  margin-bottom: 6px;
}

.bg2 {
  -webkit-border-radius: 4px;
  border-radius: 4px;
  -webkit-box-shadow:  0px 0px 3px 1px #CCC;
  box-shadow:  0px 0px 3px 1px #CCC;
  margin-top: 6px;
  margin-bottom: 6px;
}

div.buttons div a {
  padding: 0px;
  padding-left: 7px;
  padding-right: 7px;
}

div.buttons div a:hover {
  padding: 0px;
  padding-left: 7px;
  padding-right: 7px;
}

blockquote {
  margin: 1em;
  background-color: #DDE3DD;
  border: 1px solid #666;
  -webkit-border-radius: 4px;
  border-radius: 4px;
  -webkit-box-shadow:  0px 0px 4px 0px rgba(33, 33, 33, 0.5);
  box-shadow:  0px 0px 4px 0px rgba(33, 33, 33, 0.5);
}

blockquote blockquote {
  background-color: #CCD2CC;
}


blockquote blockquote blockquote {
  background-color: #BBC1BB;
}

.postbody .content embed {
  position: relative;
  width: 560px;
  left: 80px;
  -webkit-border-radius: 3px;
  border-radius: 3px;
  background-color: #000;
  border: 3px solid #000;
  -webkit-box-shadow:  0px 0px 5px 0px rgba(33, 33, 33, 0.8);
  box-shadow:  0px 0px 5px 0px rgba(33, 33, 33, 0.8);
}
}

@-moz-document url-prefix("http://games.on.net/forums/") {
body {
  background-image: url("http://i.imgur.com/KYlpp.png") !important ;
  background-size: 1920px 256px !important ;
}

.pagination span strong {
  color: white;
  background-color: #45AF48;
  border-color: #45AF48;
  -webkit-border-radius: 3px;
  border-radius: 3px;
}

.pagination span a, .pagination span a:link, .pagination span a:visited a:active {
  border-color: #45AF48;
  color: #45AF48;
  -webkit-border-radius: 3px;
  border-radius: 3px;
}

.pagination span a:hover {
  background-color: #45AF48;
  color: white;
}

li.mspace {
  box-shadow: none;
}

li.mspace_news {
  box-shadow: none;
  width: 742px;
}

p.post_tools a {
  padding: 5px 43px;
}

li.advertising {
  display: none;
  width: 0px;
}

.postprofile {
  width: 21%;
}

.panel {
  background-color: transparent;
}

ul.profile-icons li span {
  border: 1px solid #BBB;
  -webkit-border-radius: 4px;
  border-radius: 4px;
  -webkit-box-shadow:  0px 0px 2px 1px #CCC;
  box-shadow:  0px 0px 2px 1px #CCC;
  text-transform: uppercase;
}

h2 {
  font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;
  font-weight: bold;
  color: #3F3F3F;
  font-size: 2.4em;
  margin: 0.5em 0 0.5em 0;
  text-shadow: 0px 1px 4px #999;
  filter: dropshadow(color=#999, offx=0, offy=1);
}


}
