css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tumblr.com") {

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color: #217413 !important}

#dashboard_controls_suggested_blogs {background:#aaa !important; border:none !important}

#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#dashboard_members div.l-content,#main_tag div.l-content,#main_tag_beta #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background:rgba(0,0,0,0.2) !important}

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag, #main_tag_beta {background:#207412 url(http://i48.tinypic.com/2lkz2xd.jpg) no-repeat fixed !important}

#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current {background-image:none !important}

.permalink {background: rgba(0,0,0,0) !important }
#footer,.dashboard_options_form_divider {display:none}

#dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background:rgba(0,0,0,0.1) !important }

#left_column, #dashboard_controls a, #left_column #dashboard_controls span {color: #767676 !important;}

#left_column #posts .notification {background-color:rgba(0,0,0,0.1) !important;
border-bottom:none !important; border-top:none !important; border-left:none !important; border-right:none !important; color:#aaa !important}

#left_column #posts .notification.alt {background-color:rgba(0,0,0,0.16) !important;}

#left_column #posts .notification .avatar:hover {opacity:95 !important;}

#left_column #posts .notification .block,.post_info,.post_info a {color:#aaa !important}

#left_column #posts .notification a {background-color:rgba(0,0,0,0) !important; border:none !important; color:#888 !important}

#left_column #posts .notification a:hover {background-color:rgba(0,0,0,0) !important; border:none !important; color:#ececec !important}

#left_column #posts .notification blockquote {border-left-color:#bbb !important}

#left_column #posts .notification.alt, #dashboard_switch_blog_menu_footer, #dashboard_switch_blog_menu_wrapper {background#!important}

#left_column #posts .post .post_avatar,#left_column #posts .post .sub_avatar .sub_avatar_inner {background-color:#c7c4cc !important;
border-bottom:none !important}

#left_column #posts .post, .dashboard_options_form {border-bottom:none !important}



.arrow{display:none !important}



img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{width:0; height:70px; padding-right:250px; visibility: hidden;}

#nav .nav_item .nav_menu a {border-top-color:#eee !important;
color:#888 !important}

#nav .nav_item .nav_menu a:hover, .no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span, #right_column, #right_column a, .dashboard_nav_title, .dashboard_subpages, .dashboard_controls_suggested_blog_title, .dashboard_controls_suggested_blog_description {color:#767676 !important}

#nav #post .post .footer_links a:hover, #post .post .post_controls a:hover {color:#e9e9e9; !importnat}

#nav .nav_item .nav_menu, #dashboard_switch_blog_menu_header {background-color:#f7f7f7 !important; border:none !important}

#nav .nav_item .active, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel, #right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {background:#5c5c5c !important}

.nipple { border-right-color: rgba(0,0,0,0.07) !important }
.selection_nipple { border-bottom-color: rgba(0,0,0,0.1) !important }

#pagination a {background: #888 !important;color: #eee !important}

#pagination a:hover {background: #666 !important}

#posts .post .footer_links a, #posts .post .post_controls a {color:#a2adb2 !important}

#right_column li {background: transparent !important; border:0px !important;}

#right_column a:hover,.new_post_label:hover {color:#e9e9e9 !important}

#dashboard_controls_radar_media_fold #right_column .dashboard_nav_item  {background:none !important;}

#show_radar, #hide_radar, #hide_recommended_blogs, #show_recommended_blogs {background:none !important}

.dashboard_controls_radar_media {background:#aaa !important}

.dashboard_controls_suggested_blog {border:1px solid #999 !important; border-width:0 0 1px !important}

.dashboard_switch_blog_menu_item {background-color:#ececec !important; border:0 !important}

.nav_item:hover {color:#eee !important}

ol#posts li.post.new_post {background: #ffffff none center center no-repeat;}

.notification_type_icon {display:none !important}

.sub_avatar {background-color:transparent !important; height:32px !important; width:32px !important;}


h1,h2,h3,h4,h5,h6{color:#949a9d !important}

}
