css:
@-moz-document domain('duolingo.com'), url-prefix('http://duolingo.com') {
	
/*Site Background and Site Font*/
body {
	background: rgb(240,246,255) !important;
	font-family:"Segoe",Frutiger,Ubuntu,Lucida Grande,Myriad,sans-serif !important;
}

}

/*Upper Site Navigation Bar*/
.topbar-inner, .topbar .fill {
    background-color: #fff !important;
    background-image: -moz-linear-gradient(center top , rgb(0,110,255),rgb(0,110,255)) !important;
    color: #fff !important;
}
    .topbar a {
        color: rgb(255,255,255) !important;
        }
        
.menu-dropdown, .dropdown-menu {
    background-color: rgb(180, 185, 192) !important;
    float: left;
    display: none;
    position: absolute;
    top: 40px;
    min-width: 160px;
    max-width: 220px;
    margin-left: 0px;
    margin-right: 0px;
    border-radius: 0px 0px 0px 0px !important;
    padding: 6px 0px;
    border-color: rgb(204, 204, 204) !important;
    border-style: none !important;
    border-width: 0px 1px 1px;
    border-radius: 0px 0px 6px 6px;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);
    background-clip: padding-box;
    }
    .topbar div > ul .menu-dropdown li a, .nav .menu-dropdown li a, .topbar div > ul .dropdown-menu li a, .nav .dropdown-menu li a {
    color: rgb(255, 255, 255) !important;
    text-shadow: none !important;
}
/*Dropdown menu item hover highlight*/
.topbar div > ul .menu-dropdown li a:hover, .nav .menu-dropdown li a:hover, .topbar div > ul .dropdown-menu li a:hover, .nav .dropdown-menu li a:hover {
    color: rgb(255, 255, 255) !important;
    text-shadow: none !important;
    background: rgb(190, 195, 202) !important;
}

        
/*Feedbacknbox*/
#give-feedback {
    position: fixed;
    left: 0px;
    top: 350px;
    border-width: 1px 1px 1px medium;
    border-style: solid solid solid none;
    border-color: transparent !important;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    border-radius: 0px 0px 0px 0px !important;
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.2);
    cursor: pointer;
    text-indent: -10000px;
    z-index: 100;
    width: 30px;
    background: none repeat scroll 0% 0% rgb(180,188,200) !important;
}
/*Feedback box hover hightlight
It's blue because the Duolingo icon image file has the "Feedback" word as an image with a blue shadow.
*/
#give-feedback:hover {
    position: fixed;
    left: 0px;
    top: 350px;
    border-width: 1px 1px 1px medium;
    border-style: solid solid solid none;
    border-color: transparent !important;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    border-radius: 0px 0px 0px 0px !important;
    box-shadow: 0px 0px 8px rgba(0, 110, 255, 1) !important;
    cursor: pointer;
    text-indent: -10000px;
    z-index: 100;
    width: 30px;
    background: none repeat scroll 0% 0% rgb(0,110,255) !important;
}

/*Practice Button*/
.practice-this .pointer .practice-button {
    height: 64px !important;
    width: 64px;
    position: absolute;
    padding: 0px;
    border-radius: 32px 32px 32px 32px !important;
    background-image: -moz-radial-gradient(right bottom, farthest-side, #2980ff 0%, #0068ff 77%, #4d95ff 78%, #0068ff 164%) !important;
    line-height: 56px;
    text-align: center;
    border-color: rgb(0, 63, 129);
    left: 17px;
    top: 15px;

}

.practice-this .pointer .practice-button:hover {
    height: 64px !important;
    width: 64px;
    position: absolute;
    padding: 0px !important;
    border-radius: 32px 32px 32px 32px !important;
    line-height: 56px;
    text-align: center;
    border-color: rgb(0, 63, 129);
    left: 17px !important;
    top: 15px;
    background-image: -moz-radial-gradient(right bottom, farthest-side, #3d8cff 0%, #2e83ff 77%, #6ba7ff 78%, #4d95ff 164%) !important;
    box-shadow: 0px 0px 20px rgb(15,135,255) !important;
}

.practice-this .pointer .practice-button:active {
    height: 64px !important;
    width: 64px;
    position: absolute;
    padding: 0px !important;
    border-radius: 32px 32px 32px 32px !important;
    line-height: 56px;
    text-align: center;
    border-color: rgb(0, 63, 129);
    left: 17px !important;
    top: 15px;
    background-image: -moz-radial-gradient(right bottom, farthest-side, #3d8cff 0%, #2e83ff 77%, #6ba7ff 78%, #4d95ff 164%) !important;
    box-shadow: 0px 0px 50px rgb(15,135,255) !important;
}

/*Right Panel*/

/*Profile box*/
.mini-profile {
    padding: 30px 130px 65px 20px;
    margin: 0px -20px;
    background: rgb(245, 247, 250) !important;
    border-radius: 0px 5px 0px 0px !important;
    min-height: 119px;
}
/*Language Progress (2nd panel)*/
.language-progress {
    box-shadow: none !important;
    background: rgb(240, 242, 246) !important;
    border: none !important;
    }
/*Daily Progress Chart (3rd panel)*/
.daily-progress {
    box-shadow: none !important;
    background: rgb(235, 237, 242) !important;
    border: none !important;
    }
    /*Chart*/
        .progress-calendar {
        width: 297px;
        border: none !important;
        border-radius: 0px 0px 0px 0px !important;
        list-style: none outside none;
        margin: 0px 0px 16px;
        transition: 1s !important;
        }
        /*chart glow effect when hovered on*/
        .progress-calendar:hover {
        box-shadow: 0px 0px 15px rgb(255,255,255) !important;
        }
        /*Invisible Vertical Grid lines*/
        .progress-calendar > li {
        text-align: center;
        width: 42px;
        border-left: 1px solid rgb(255, 255, 255) !important;
        float: left;
        position: relative;
        background: none repeat scroll 0% 0% white;
        }
        /*Sharp corners*/
        .progress-calendar > li:first-child {
        border-radius: 0px 0px 0px 0px !important;
        border-left: 0px !important;
        }
        .progress-calendar > li:last-child {
        border-radius: 0px 0px 0px 0px !important;
        }
        /*Horozontal divider line*/
        .progress-calendar > li strong.day {
        border-bottom: 1px solid rgb(235, 237, 242) !important;
        font-weight: bold;
        }
        /*Current Day text hover effect*/
        .progress-calendar > li strong.day:hover {
        font-weight: bold;
        text-shadow: 0px 0px 5px rgba(153,153,153,0.5) !important;
        }
        /*Current Day box & sharp corners*/
        .progress-calendar > li.current strong.day {
        color: rgb(255, 255, 255) !important;
        background: none repeat scroll 0% 0% rgb(15, 125, 255) !important;
        border-radius: 0px 0px 0px 0px !important;
        transition: 1s !important;
        }
        .progress-calendar > li.current strong.day:hover {
        color: rgb(255,255,255) !important;
        background: none repeat scroll 0% 0% rgb(100, 180, 255) !important;
        border-radius: 0px 0px 0px 0px !important;
        box-shadow: 0px 0px 15px rgb(150, 200, 255) !important;
        text-shadow: 0px 0px 5px rgba(255,255,255,0.5) !important;
        }
/*Invite Friends (4th panel)*/
.invite-friends {
    border: none !important;
    margin: -20px -20px 19px -19px !important;
    padding: 20px 20px !important;
    box-shadow: 0px 30px 0px rgb(230, 233, 237) !important;
    background: rgb(230, 233, 237) !important;
}
/*Actually white box*/
.gray-box {
border-radius: 0px 0px 0px 0px !important;
border: none !important;
}
/*Whole App background*/
#app.profile {
background: rgb(245, 247, 250) !important;
border: none !important;
}
/*Skill Tree background*/
.row .columns {
background: rgb(255, 255, 255) !important;
background-color: rgb(255, 255, 255) !important;
box-shadow: 0px 0px 40px rgb(255,255,255) !important;
}

            
/*skill box*/
.skill-box {
    border: 0px !important;
    border-radius: 0px 0px 0px 0px !important;
    background-image: -moz-linear-gradient(center top , rgb(226, 231, 240), rgb(226, 231, 240)) !important;
    box-shadow: 0px 0px 0px rgba(255, 255, 255, 0.4), 0px 0px 0px rgb(255, 255, 255) inset !important;
    }
    
    /*Removes screws from locked skill boxes*/
    .skill-box.locked .screws {
    display: none !important;
    }

    /*Mastered Skill Icon Boxes*/
        /*This replaces the image background with a css shily gold background*/
        .skill-box.mastered .header {
            background: transparent !important;
            border: 0px !important;
            background-image: -moz-linear-gradient(left top, #fec93e 0%, #ffe642 52%, #ffb619 53%, #fec93e 100%) !important;
            box-shadow: 0px 0px 0px rgba(255, 255, 255, 0.4), 1px 1px 0px rgb(255, 255, 255) inset !important;
            }
            /*Mastered Skill Glow*/
            .skill-box.mastered .header:hover {
            background: transparent !important;
            border: 0px !important;
            background-image: -moz-linear-gradient(left top, #fec93e 0%, #ffe642 52%, #ffb619 53%, #fec93e 100%) !important;
            box-shadow: 0px 0px 20px rgba(255, 210, 0, 1), 1px 1px 0px rgba(255, 240, 50,1) inset !important;
            }
            /*"Mastered" plate*/
            .mastered-badge {
            border: none !important;
            background-image: -moz-linear-gradient(center top , rgb(240, 245, 252), rgb(240, 245, 252)) !important;
            color: rgb(179,140,46) !important;
            border-radius: 0px 0px 0px 0px !important;
            text-shadow: none !important;
            box-shadow: none !important;
            font-weight: bold !important;
                }
                /*"Mastered" text hover glow effect*/
                .mastered-badge:hover {
