                text-shadow: 0px 0px 10px rgb(220,170,0) !important;
                }
            
    /*Unlocked Skill Icon Boxes(Colored boxes and hover glows)*/
    
        .skill-box .header {
        box-shadow: 0px 0px 0px rgba(255, 255, 255, 0.4), 1px 1px 0px rgb(255, 255, 255) inset !important;
        border: 0px solid rgba(0, 0, 0, 0.3) !important;
        border-radius: 0px 0px 0px 0px !important;
        }
    
            /*Red Basics*/
            .new-useful-stuff .header, .new-useful-stuff.unlocked .title, .new-useful-stuff.category-badge, .new-useful-stuff .mastered-small {
            background-color: rgb(222, 97, 30);
            background-repeat: repeat-x;
            background-image: -moz-linear-gradient(center top , rgb(255, 90, 43), rgb(255, 90, 43)) !important;
            }
            .new-useful-stuff .header:hover, .new-useful-stuff.unlocked .title:hover, .new-useful-stuff.category-badge:hover, .new-useful-stuff .mastered-small:hover {
            background-color: rgb(222, 97, 30);
            background-repeat: repeat-x;
            background-image: -moz-linear-gradient(center top , rgb(255, 100, 53), rgb(255, 100, 53)) !important;
            box-shadow: 0px 0px 20px rgb(255, 100, 53), 1px 1px 0px rgb(255, 130, 83) inset !important;
            }

            /*Green Vocab*/
            .new-vocabulary .header, .new-vocabulary.unlocked .title, .new-vocabulary.category-badge, .new-vocabulary .mastered-small {
            background-color: rgb(156, 179, 62) !important;
            background-repeat: repeat-x;
            background-image: -moz-linear-gradient(center top , rgb(156, 200, 62), rgb(156, 200, 62)) !important;
            }
            .new-vocabulary .header:hover, .new-vocabulary.unlocked .title:hover, .new-vocabulary.category-badge:hover, .new-vocabulary .mastered-small:hover {
            background-color: rgb(156, 179, 62) !important;
            background-repeat: repeat-x;
            background-image: -moz-linear-gradient(center top , rgb(166, 210, 72), rgb(166, 210, 72)) !important;
            box-shadow: 0px 0px 20px rgb(166,210,72), 1px 1px 0px rgb(196, 240, 102) inset !important;
            }
        
            /*Blue Grammar*/
            .new-grammar .header, .new-grammar.unlocked .title, .new-grammar.category-badge, .new-grammar .mastered-small {
            background-color: rgb(33, 131, 188);
            background-repeat: repeat-x;
            background-image: -moz-linear-gradient(center top , rgb(50,150,255), rgb(50,150,255)) !important;
            }
            .new-grammar .header:hover, .new-grammar.unlocked .title:hover, .new-grammar.category-badge:hover, .new-grammar .mastered-small:hover {
            background-color: rgb(33, 131, 188);
            background-repeat: repeat-x;
            background-image: -moz-linear-gradient(center top , rgb(60,170,255), rgb(60,170,255)) !important;
            box-shadow: 0px 0px 20px rgb(60,170,255), 1px 1px 0px rgb(90,200,255) inset !important;
            }
        
            /*Turquoise Verbs*/
            .new-verbs .header, .new-verbs.unlocked .title, .new-verbs.category-badge, .new-verbs .mastered-small {
            background-color: rgb(133, 200, 190);
            background-repeat: repeat-x;
            background-image: -moz-linear-gradient(center top , rgb(146, 213, 220), rgb(146, 213, 220)) !important;
            }
            .new-verbs .header:hover, .new-verbs.unlocked .title:hover, .new-verbs.category-badge:hover, .new-verbs .mastered-small:hover {
            background-color: rgb(133, 200, 190);
            background-repeat: repeat-x;
            background-image: -moz-linear-gradient(center top , rgb(156, 223, 230), rgb(156, 223, 230)) !important;
            box-shadow: 0px 0px 20px rgb(156, 223, 230), 1px 1px 0px rgb(186, 253, 255) inset !important;
            }

        
