/*Lessons*/

/*Skill Home*/

/*Top title bar*/
.standard-head {
    margin: 0px -20px 20px;
    min-height: 60px;
    border-bottom: 0px solid rgb(205, 205, 205) !important;
    position: relative;
    border-radius: 5px 5px 0px 0px;
    background-color: rgb(225, 225, 225);
    background-repeat: repeat-x;
    background-image: -moz-linear-gradient(center top , rgb(230,233,237), rgb(230, 233, 237)) !important;
}

/*Darkening "Test out this Skill" text*/
.btn.test-out .attempts {
    color: rgb(60, 70, 85) !important;
}

/*Color-code tab on the side of the title bar*/
.category-badge {
    width: 14px !important;
    height: 60px;
    border: 0px solid rgb(136, 136, 136) !important;
    border-radius: 5px 0px 0px 0px;
    background: none repeat scroll 0% 0% rgb(204, 204, 204);
    position: absolute;
    left: -1px;
    top: -1px;
    }

/*Lesson*/

/*Top panel*/
.player-header {
    background-image: -moz-linear-gradient(center top , rgb(230,233,237), rgb(230, 233, 237)) !important;
    }

/*Progress Bar Empty*/
ul.progress-small {
    margin: 24px 0px 0px 20px;
    background-color: rgb(213, 213, 213) !important;
    background-repeat: no-repeat;
    background-image: linear-gradient(rgb(200, 205, 212), rgb(200, 205, 212) 25%, rgb(200, 205, 212)) !important;
    border-radius: 3px 3px 3px 3px !important;
    border: 0px solid rgb(195, 200, 207) !important;
    padding: 0px;
    list-style: none outside none;
    float: left;
    box-shadow: 0px 0px 0px rgb(255, 255, 255) !important;
    }
    
    /*Lesson Progress Tiles*/
    ul.progress-small li.done {
    border-width: 0px 0px 0px medium !important;
    border-style: solid solid solid none;
    border-color: rgba(95, 149, 0, 0) rgba(95, 149, 0, 0) rgba(95, 149, 0, 0) -moz-use-text-color !important;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    border-radius: 1px 1px 1px 1px !important;
    background-color: rgb(111, 174, 5);
    background-repeat: repeat-x;
    background-image: -moz-linear-gradient(center top , rgb(255,255,255), rgb(255,255,255)) !important;
    box-shadow: 0px 0px 0px rgba(255, 255, 255, 0.3) inset !important;
    margin: 0px 0px !important;
    box-shadow: 0px 0px 5px rgba(0, 200, 255, 1) !important;
    }

    /*shifts first tile*/
        ul.progress-small li.done:first-child {
        margin-left: -1px;
        border-left: 0px solid rgb(95, 149, 0) !important;
        border-radius: 3px 0px 0px 3px;
        }
        


/*Buttons*/

/*Azure Active Buttons*/
.btn.success, .alert-message.success {
    background-color: rgb(0,110,255) ;
    border-radius: 0px 0px 0px 0px !important;
    border: none !important;
    background-repeat: repeat-x;
    background-image: -moz-linear-gradient(center top , rgb(0,110,255), rgb(0,110,255)) !important;
    text-shadow: none !important;
    }

/*Azure Buttons Hover*/
 .btn.danger:hover, .alert-message.danger:hover, .btn.error:hover, .alert-message.error:hover, .btn.success:hover, .alert-message.success:hover, .btn.info:hover, .alert-message.info:hover {
    background-image: -moz-linear-gradient(center top , rgb(15,135,255), rgb(15,135,255)) !important;
}
/*Azure Buttons Pushed Glow*/
.btn.danger:active, .alert-message.danger:active, .btn.error:active, .alert-message.error:active, .btn.success:active, .alert-message.success:active, .btn.info:active, .alert-message.info:active {
    background-image: -moz-linear-gradient(center top , rgb(55,200,255), rgb(55,200,255)) !important;
    box-shadow: 0px 0px 20px rgba(55, 200, 255, 0.5) !important;
}
    
/*Disabled Buttons*/
    .btn[disabled] {
    background: none repeat scroll 0% 0% transparent;
    box-shadow: none;
    background-image: -moz-linear-gradient(center top , rgb(195,203,215), rgb(195,203,215)) !important;
    color: rgb(255, 255, 255) !important;
    text-shadow: none;
    border: 0px !important;
    }
/*Disabled hover(Prevents Azure Hover from applying to gray buttons*/
    .btn[disabled]:hover {
    background-image: -moz-linear-gradient(center top , rgb(195,203,215), rgb(195,203,215)) !important;
    }
    
/*Picture Slides ("Hint: it's the translation of..." boxes)*/
.alert-message {
    background-color: rgb(238, 220, 148) !important;
    background-repeat: repeat-x;
    background-image: -moz-linear-gradient(center top , rgb(255,255,255), rgb(255,255,255)) !important;
    margin-bottom: 18px ;
    padding: 7px 14px ;
    color: rgb(64, 64, 64) !important;
    text-shadow: none !important;
    border: none !important;
    border-radius: 0px 0px 0px 0px !important;
    box-shadow: 0px 0px 20px rgba(255, 220, 10, 0.5) !important;
}

/*Accent Keyboard*/

/*Show/Hide Accent Keyboard Button*/
.button-vkeyboard {
    background-image: -moz-linear-gradient(center top , rgb(200,205,212), rgb(200,205,212)) !important;
    cursor: pointer;
    border: 1px solid rgb(183, 183, 183);
    height: 15px !important;
    border-radius: 0px 0px 0px 0px !important;
    /*Makes button bigger*/
    border: 1px solid rgb(200,205,212) !important;
    /*Positioning*/
    top: 5px !important;
    right: 10px;
    padding: 3px 5px 5px 5px !important;
    margin-top: 0px !important;
    
    color: rgb(64, 64, 64);
    line-height: 22px;
    font-size: 13px;
    font-weight: bold;
    text-shadow: none !important;
    box-shadow: none !important;
}

/*Accent Keyboard Keys*/
.vkeyboard_key {
    background-color: rgb(225, 225, 225) !important;
    background-repeat: repeat-x;
    background-image: -moz-linear-gradient(center top , rgb(200,205,212), rgb(200,205,212)) !important;
    cursor: pointer;
    border-radius: 0px 0px 0px 0px !important;
    box-shadow: none !important;
    /*Makes button bigger*/
    border: 3px solid rgb(200,205,212) !important;
    
    width: 22px;
    height: 22px;
    line-height: 22px;
    float: left;
    text-align: center;
    margin: 2px 3px 0px 3px !important;
    color: rgb(64, 64, 64) !important;
    font-weight: normal !important;
    font-size: 18px !important;
    padding: 3px 0px 0px 0px !important;
    }
    /*Shift Key*/
    #vkeyboard_shift_up {
    position: relative;
    padding: 3px 0px 0px 20px !important;
    width: 40px;
    }
    #vkeyboard_shift_down {
    position: relative;
    padding: 3px 0px 0px 20px !important;
    width: 40px;
    }
    /*Key hover effect*/
    .vkeyboard_key:hover {
    border: 3px solid rgb(215,220,227) !important;
    background-image: -moz-linear-gradient(center top , rgb(215,220,227), rgb(215,220,227)) !important;
    }


/*text input*/
/*Translate Sentence Text Box*/
.translation-textarea {
    width: 398px;
    line-height: 30px !important;
    padding: 10px 50px 10px 10px;
    font-size: 20px !important;
    font-family: "Maritime Tropical", Burst my Bubble, Segoe Print, sans-serif !important;
    color: rgb(64, 64, 64);
    }
    .translation-textarea:hover {
    border-color: rgba(0,110,255,0.5) !important;
    }
/*Listen and Type*/
.known.listen .one-line-input {
    font-size: 20px !important;
    font-family: "Maritime Tropical", Burst my Bubble, Segoe Print, sans-serif !important;
    color: rgb(64, 64, 64);
    padding: 6px 50px 5px 8px !important;
    height: 30px !important;
    width: 396px;
}

.one-line-input {
    font-size: 20px !important;
    font-family: "Maritime Tropical", Burst my Bubble, Segoe Print, sans-serif !important;
    height: 30px !important;
}
/*text box hover color change*/
.known.listen .one-line-input:hover {
    border-color: rgba(0,110,255,0.5) !important;
    }
.one-line-input:hover {
border-color: rgba(0,110,255,0.5) !important;
    }
    
    
input, select, textarea {
