@charset "utf-8";
  /* CSS Document */
  
  @namespace url(http://www.w3.org/1999/xhtml);
  
  @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {
  
  #logo {height: 0 !important;
  
  	width: 200 !important;
  
  	padding-left: 230px !important;
  
  	padding-top: 64px !important;
  
  	background: url(http://img707.imageshack.us/img707/9898/logoeric.png) no-repeat !important;}
  
  
  body {
  
      background: url(http://img560.imageshack.us/img560/6765/cosmopolitaneric.jpg)
  
      top center
  
      #221613
  
      no-repeat fixed !important;
  
      font-family: helvetica, arial, sans-serif;
  
      font-size:11px !important;
  
    }
  
  
  
  ol#posts li blockquote,
  
  
  
  body.mceContentBody blockquote {
  
  margin-left: 0 !important;
  
  margin-right: 0 !important;
  
  padding-left: 10px !important;
  
  border-width: 4px !important;
  
  border-color: #684133	!important;
  
  
  
  }
  
  
  
  ol#posts li blockquote blockquote,
  
  body.mceContentBody blockquote blockquote {
  
  border-color: #684133	!important;
  
  }
  
  ol#posts li blockquote blockquote blockquote,
  
  body.mceContentBody blockquote blockquote blockquote {
  
  border-color: #684133	!important;
  
  }
  
  ol#posts li blockquote blockquote blockquote 
  
  
  
  blockquote,
  
  body.mceContentBody blockquote blockquote blockquote 
  
  
  
  blockquote {
  
  border-color: #684133	!important;
  
  }
  
  ol#posts li blockquote blockquote blockquote 
  
  
  
  blockquote blockquote,
  
  body.mceContentBody blockquote blockquote blockquote 
  
  
  
  blockquote blockquote {
  
  border-color: #684133	!important;
  
  }
  
  ol#posts li.notification.single_notification {
  
  border-bottom: 0px solid #808080;
  
  }
  
  
  
  #dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  
      background: rgba(0,0,0,0) !important;
  
  }
  
  
  
  *{text-shadow:none !important; color:#684133	!
  
  
  
  important;}
  
  
  
  .post_question, 
  
  #left_column #posts .post .permalink {
  
      background: rgba(0,0,0,0) !important;
  
  }
  
  
  
  .post_question_nipple, .arrow{
  
      background:transparent!important;}
  
  
  
  a, a:visited{color:#684133	!important}
  
  a:hover{color:#895442 !important}
  
  
  
  
  
  #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  
      background: rgba(0,0,0,0) !important;
  
      -webkit-border-radius: 20px;
  
      -moz-border-radius: 20px;
  
      border-radius: 20px;
  
      margin: auto;
  
      padding: 40px auto;
  
    }
  
  
  
  h1 {
  
  color: #684133;
  
  text-shadow: 0 0 0px #684133;
  
  }
  
  
  
  .no_posts_found, h1.dashboard_header {
  
  color: #684133;
  
  }
  
  
  
  img#content_top, img#content_bottom {
  
  display: none;
  
  }
  
  
  
  #right_column a#dashboard_switch_blog_menu_current 
  
  
  
  #dashboard_switch_blog_arrow_current {
  
      background: none;
  
    }
  
  
  
  #right_column .dashboard_nav_item {
  
      background-image:none
  
  }
  
  
  
  #right_column .dashboard_nav_item #hide_radar {
  
  background-image: none;
  
  background-repeat-x: no-repeat;
  
  background-repeat-y: repeat;
  
  background-attachment: initial;
  
  background-position-x: 0%;
  
  background-position-y: 0%;
  
  background-origin: initial;
  
  background-clip: initial;
  
  background-color: transparent;
  
  }
  
  
  
  #right_column .dashboard_nav_item 
  
  
  
  .dashboard_controls_radar_media.photo, 
  
  
  
  #right_column .dashboard_nav_item 
  
  
  
  .dashboard_controls_radar_media.photoset, 
  
  
  
  #right_column .dashboard_nav_item 
  
  
  
  .dashboard_controls_radar_media.video {
  
  background: none;
  
  }
  
  
  
  #nav .nav_item.active {
  
       background: rgba(0,0,0,0) !important;
  
    }
  
  
  
  #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel 
  
  
  
  {
  
       background: rgba(0,0,0,0) !important;
  
    }
  
  
  
  ol#posts li.notification.alt {
  
      background-color: rgba(0,0,0,0);
  
  }
  
  
  
  ol#posts li.notification.first_notification {
  
      background-color: rgba(0,0,0,0);
  
  }
  
  
  
  ol#posts li.notification {
  
      background-color: rgba(0,0,0,0);
  
      border-bottom: 0px solid #684133;
  
  }
  
  
  
  ol#posts li.notification.last_notification {
  
      border-bottom: 0px solid #684133;
  
  }
  
  
  
  ol#posts li.post .permalink {
  
      background: rgba(0,0,0,0);
  
  }
  
  
  
  #right_column .dashboard_nav_item 
  
  
  
  #dashboard_controls_suggested_blogs {
  
  background: rgba(0,0,0,0);
  
  border: solid #684133;
  
  }
  
  
  
  ol#posts li.post .post_avatar{
  
  border-bottom: 0px solid #684133;
  
  }
  
  
  
  #dashboard_switch_blog_menu_current,
  
  #dashboard_switch_blog_arrow_and_divider,
  
  #dashboard_switch_blog_arrow_current
  
  {background-image:none !important}
  
  
  
     ol#posts li.post.new_post {
  
      background: rgba(0,0,0,0)
  
      center center no-repeat;
  
  }
  
  
  
  img
  
  
  
  [src*="http://assets.tumblr.com/images/new_post_icons_
  
  
  
  no_labels.png"]{
  
      width:0;
  
      height:70px;
  
      padding-right:250px;
  
      opacity: 0;
  
  }
  
  
  
  #right_column .dashboard_nav_item {
  
  border-bottom:0px solid #684133 !important;
  
  background-image:none  !important
  
  }
  
  
  
  #right_column .dashboard_nav_item 
  
  
  
  #dashboard_controls_suggested_blogs 
  
  
  
  .dashboard_controls_suggested_blog {
  
  border-top: 0px solid #684133;
  
  border-bottom: 0px solid #684133;
  
  }
  
  
  
  #right_column 
  
  
  
  a#dashboard_switch_blog_menu_current:hover {
  
  background: none;
  
  }
  
  
  
  #right_column 
  
  
  
  a#dashboard_switch_blog_menu_current:hover 
  
  
  
  #dashboard_switch_blog_arrow_current {
  
  background: none;
  
  }
  
  
  
  form.dashboard_options_form {
  
  background-color: rgba(0,0,0,0);
  
  border-bottom: 0px solid #684133;
  
  border-bottom-left-radius: 10px 10px;
  
  border-bottom-left-radius: 10px 10px;
  
  border-bottom-right-radius: 10px 10px;
  
  border-bottom-right-radius: 10px 10px;
  
  border-top-left-radius: 10px 10px;
  
  border-top-left-radius: 10px 10px;
  
  border-top-right-radius: 10px 10px;
  
  border-top-right-radius: 10px 10px;
  
  color: #684133;
  
  display: block;
  
  font-size: 13px;
  
  margin-bottom: 25px;
  
  padding: 15px 22px;
  
  }
  
  
  
  form.dashboard_options_form .option_container {
  
  border-top: 0px solid #684133;
  
  }
  
  
  
  .no_posts_found, h1.dashboard_header {
  
  color: #684133;
  
  }
  
  
  
  .answer_container {
  
  background-color: #684133;
  
  border-bottom: 0px solid #684133;
  
  }
  
  
  
  #nav .nav_item .nav_menu {
  
  background-color: #684133;
  
  border-bottom: 0px solid #684133;
  
  }
  
  
  
  #nav .nav_item .nav_menu a {
  
  border-top: 0px solid #684133;
  
  }
  
  
  
  #auto_pagination_loader {
  
  background: rgba(0,0,0,0) none 
  
  
  
  repeat-x 50% 0%;
  
  border-top: 0px solid #684133;
  
  }
  
  }
  
  
  
