css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www.kongregate.com") {



#challenge-description, #nav-botd-description, #nav-botd-a, a#nav-botd-game-name.badge_link, badge_of_the_day_description a{

color: #fff !important;

}

div.compact_feature_game.clickable{

-webkit-border-radius: 0px 10px 0px 10px;

border-radius: 0px 10px 0px 10px;    

}

div.clickable{

-webkit-border-radius: 0px 10px 0px 10px;

border-radius: 0px 10px 0px 10px; 

}

div#featured_games_pod.mhm{

-webkit-border-radius: 0px 10px 0px 10px;

border-radius: 0px 10px 0px 10px;       

}



div#featured_content{

-webkit-border-radius: 0px 10px 0px 10px;

border-radius: 0px 10px 0px 10px; 

}

div#subwrap{

border-top:5px solid #191919 !important;

}





td#skin_left_of_game{

background-position: absolute !important;

background:url("http://www.dorisdam.se/style/KKBGSIDES.jpg") fixed!important;

}



td#skin_right_of_game{

background:url("http://www.dorisdam.se/style/KKBGSIDES.jpg") fixed!important;

}





td.maincontent{

border-right: 5px solid #222;

border-left: 5px solid #222;

}



.nav-tab-collection .nav-tab-pane{

background-color: #191919 !important;

}

.nav-tab-collection .nav-tab-pane dt{

color: #fff !important;

}









.nav-tab-pane{

  border-left:1px solid #000;

  border-right:1px solid #000;

  border-bottom:1px solid #000;

  border-radius: 6px;

  -webkit-border-radius: 6px;

  -moz-border-radius: 6px;

  box-shadow: 5px 5px 4px rgba(0,0,0,0.5);

  /*-o-box-shadow: inset 0 -20px 40px #aaa, inset 0 20px 40px #fff, 0 2px 6px rgba(0,0,0,0.5);

  -webkit-box-shadow: inset 0 -20px 40px #aaa, inset 0 20px 40px #fff, 0 2px 6px rgba(0,0,0,0.5);

  -moz-box-shadow: inset 0 -20px 40px #aaa, inset 0 20px 40px #fff, 0 2px 6px rgba(0,0,0,0.5);*/

}





.nav-tab-pane dt span{

background-color: #666 !important;

}

.nav-tab-pane dt{

border-bottom: 1px solid #666 !important;

}

#search{

background-color: #191919 !important;

}

.nav_tab_name a{

background-color: #191919 !important;

}

#headerwrap{

background-image:url("http://www.dorisdam.se/style/KKGBG.jpg")  !important;

border-top: 5px solid #111 !important;

border-bottom: 5px solid #191919 !important;

}

#welcome_box_small_user_avatar{

-webkit-border-radius: 0px 0px 0px 5px;

border-radius: 0px 0px 0px 5px;    

}

#welcome{

-webkit-border-radius: 0px 0px 5px 5px;

border-radius: 0px 0px 5px 5px;        

background-color: #191919 !important;

}



#profile_bar_messages{

background-color: #191919 !important;

border-left: 5px solid #111 !important;

-webkit-border-radius: 0px 0px 5px 5px;

border-radius: 0px 0px 5px 5px;   

}



#profile_bar{

border-left: 5px solid #111 !important;

}



#old_school_profile_tab_exposed dl dt .username_holder{

color:#fff !important;

/*http://www.dorisdam.se/style/nav_sprite.png*/

}

.spritenav{ 

background-image:url("http://www.dorisdam.se/style/nav_sprite22.png")  !important;

}

a.spriteall.spritenav{

-webkit-border-radius: 7px 0px 0px 0px;

border-radius: 7px 0px 0px 0px;  

background-image:url("http://www.dorisdam.se/style/nav_sprite22.png")  !important;

}

.adcontainer{

display: none !important;

}

#category .vertical_ad{

display: none !important;

}

.horisontal_ad{

display: none !important;

}

#category .adspacer{

display: none !important;

}

#kong_home_af_300x250-ad-slot.ad-container{

display: none !important;

}



}
