css:
@-moz-document domain(youtube.com) {

/* Remove sidebar and extend description */
#watch-sidebar { display: none; }
#watch-panel, #watch-actions {
	width: 100% !important;
	float: none !important;
}
#watch-description-clip { width: 700px !important; }

/* Move votes */
#watch-description-extra-info {
	position: static !important;
	float: right !important;
	margin-left: 0 !important;
	margin-top: 10px;
	top: 353px !important;
	left: 480px !important;
}

/* Hide comments */
#watch-discussion { display: none; }

/* Extend player */
#watch-player {
	width: 970px !important;
	height: 572px !important;
}

/* And apply YouTube's styling */
#watch-video-container {
	background-color: #444;
	background-image: -moz-linear-gradient(top,#555,#333); /* Eh... */
	background-image: -webkit-gradient(linear,left top,left bottom,from(#555),to(#333));
}

}
