css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/dashboard'),
url-prefix('http://www.tumblr.com/blog') , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog") {

#posts_control .posts:before,
#dashboard_controls_open_blog > li .followers:before,
#dashboard_controls_open_blog > li .drafts:before,
#dashboard_controls_open_blog > li .queue:before {
  position: absolute !important;
  content: "A Lot" !important;
  width:165px !important;
  height: 30px !important;
  text-align: right !important;
  font: inherit !important;
  color: inherit !important;
  opacity: .5 !important;
}
#dashboard_controls_open_blog > li .followers:before {
  content: "Enough" !important;
}
#dashboard_controls_open_blog > li .drafts:before {
  content: "Draft?" !important;
}
#dashboard_controls_open_blog > li .queue:before {
  content: "?????" !important;
}
#posts_control .count,
#dashboard_controls_open_blog > li .followers .count,
#dashboard_controls_open_blog > li .drafts .count,
#dashboard_controls_open_blog > li .queue .count {
  visibility: hidden !important;
}
#posts_control a:hover:before,
#dashboard_controls_open_blog > li .followers:hover:before,
#dashboard_controls_open_blog > li .drafts:hover:before,
#dashboard_controls_open_blog > li .queue:hover:before {
  display: none !important;
}
#posts_control:hover .count,
/*.posts:hover .count,
.followers:hover .count,
.drafts:hover .count,
.queue:hover .count,*/
#dashboard_controls_open_blog > li .followers:hover .count,
#dashboard_controls_open_blog > li .drafts:hover .count,
#dashboard_controls_open_blog > li .queue:hover .count {
  visibility: visible !important;
}
}
