css:
/* livedoorreader */

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(https://member.livedoor.com/login/),
               url-prefix(http://member.livedoor.com/login/),
               url-prefix(http://reader.livedoor.com/reader/){

/* header */
#headerWrapper{
  display:none;
}

/* footer */
#footer{
  display:none;
}

/* main */
#loginAdwall{
  display:none;
}

body #mainContent #loginContent {
  left: 370px!important;
}

/* font */
* {font-family:Hiragino Kaku Gothic Pro,"ヒラギノ角ゴ Pro W3","メイリオ",Meiryo,Osaka,"ＭＳ Ｐゴシック", sans-serif !important;}

/* monospace-font IPAMonaGothic */
pre, pre *, code, samp, var, tt   {
  font-family:IPAMonaGothic !important;
}

/* ヘッダ */
#header,
#reader_logo{
  display:none;
}

/* ガイド */
  #guiderankbody{
  display:none;
}

/* 広告 */
#megaText,#ads_top,.ads,#header,.r25_box{
  display:none !important;
}

/* 文字装飾 */
.fontpad{
  display:none;
}

/* 棒人間 */
#message_box{
  position:absolute;
  left:5px;
}

/* フォルダを開いたときにスクロールバーでずれない */
#subs_container{
  overflow-y:scroll !important;
}

/* コントロールエリア */
#left_container{
  left:auto !important;
  right:0 !important;
}

#subs_container {
  background-color:#f5f5f5 !important;
}

#right_container {
  left:0 !important;
  right:auto !important;
}

/* スクロールバーを右表示 */
#subs_container{
  direction: ltr !important;
}

/* 記事見出し */
.channel{
  display:none;
}

/* ナビゲーション上 */
#right_top_navi{
  display:none !important;
}

.item_body a img{
  border:none;
}

.item_body img + br{
display:none;
}

.item_body ol,.item_body .item_body hr,.item_body pre,.item_body blockquote,.item_body table,.item_body ul,.item_footer{
clear:left !important;
}

/* 大きすぎる画像 */
div.body img {
  max-height: 500px !important;
}

/* フィード周りを1ライン表示 */
.channel_toolbar{
  background-color:#fff !important;
  border:none !important;
  margin:0 !important;
  height:0 !important;
}

.channel_toolbar table{
  position:fixed;
  top:50px;
  right:0;
}

.channel_toolbar table + table{
  top:50px;
  right:300px !important;
  width:200px; 
}

/* フォルダ変更 */
.FlatMenu div{
  width:200px !important;
  height:600px !important;
  top:77px !important;
  position:fixed !important;
  overflow-y:scroll !important;
  border:2px solid #eee !important;
}

#loginContent{
  float:none !important;
}

a[href^="http://ecx.images-amazon.com"],
a[href^="http://www.amazon.co.jp/exec/"],
a[href^="http://rss.rssad.jp/rss/ad/"]{
  display :none;
}

/* ピン */
.pin_item{
}

/* hateb */
a[href^="http://b.hatena.ne.jp/entry/"]{
  display:none;
}
}
