css:
body {
	background-color: #e7ecf2;
}


/*--------------------------------------- Layout ---------------------------------------*/
.fbx #globalContainer {
	width: 1200px;
}

div#leftCol {
	background-color: #fff;
	border-bottom: 1px solid #b3b3b3;
	border-left: 1px solid #b3b3b3;
	padding: 20px 0 15px 15px;
	width: 178px;
}

div#rightCol {
	display: none;
}

body.hasLeftCol div#contentCol {
	margin-left: 194px;
}

.hasLeftCol .homeWiderContent div#contentArea {
	width: 960px;
}

.Locale_ja_JP .uiStream .uiStreamMessage {
}

/* main */
div.mainWrapper {
}

div.storyInnerContent {
}

.uiStreamMessage {
	width:  400px;
}

h6.uiStreamMessage {
	float: left;
}

/* title */
h6.uiStreamMessage.uiStreamHeadline {
	float: none;
}

div.uiStreamAttachments {
	clear: left;
	float: left;
	width: 400px;
}

/* 引用 */
a.actorPhoto.UIImageBlock_Image + div.storyInnerContent ul.uiCollapsedList .uiStreamMessage,
a.actorPhoto.UIImageBlock_Image + div.storyInnerContent ul.uiCollapsedList div.uiStreamAttachments {
	width: 360px;
}

/* line */
div.uiStreamEdgeStoryLine {
	clear: left;
}

/* like comment fomr */
form.commentable_item {
	overflow: auto;
	padding-left:  50px;
}

div#pagelet_reminders {
	display: none;
}

div#pagelet_rhc_footer {
	display: none;
}

/* Adsense */
div#pagelet_ego_pane_w {
	display: none;
}
