css:
@-moz-document domain("forum.motorsport-total.com") {
html body *	{	font-family: verdana,seriv !important;background-image:none !important;	}
body #page_margins {margin:0 !important;min-width:936px!important;max-width:1650px!important} 
#nav, #nav_main, #left_menu, #menu_item, #ivw, #skyscraper, #header, #page_margins1, #headerinfo, 
#logo, #col1, #weissraum, #shadow_footer, #footer {    display: none !important;  }
#mainb {background-color:#FFFFFF !important;  background-image:none !important;  }
html body a:link { color:#000099 !important;}
html body a:visited { color:#000030 !important;}
html body #col3 {margin:0 31px 0 12px !important; }

html body tr td table tbody tr td span.txt11 a.opfr img {-moz-opacity: 0.5 !important; opacity: 0.5 !important;}
html body tr td table tbody tr td span.txt11 a.opfr:hover img {-moz-opacity: 1 !important; opacity: 1 !important;}

html body tr td table tbody tr td div.post blockquote {   background-color:#F0F0FF !important; }
html body tr td table tbody tr td {height:36px  !important; }
html body tr td table tbody tr td a { font-size:15px  !important; }
html body tr td table tbody tr td span.txt11 a.pfr 
{ background:url(http://forum.motorsport-total.com/i/pfr.gif) no-repeat left center; font-size:12px !important;}
html body tr td table tbody tr td div a {font-size:11px  !important; }
html body tbody th {background-color:#F0F0FF; font-size:16px  !important; }

html tbody tr td textarea {background-color:#FFFFFF; border:1px;border-color:#000055; border-style:solid; 
font-family: Arial,seriv !important; width:600px; height:200px; padding:5px;}

html form table tbody tr td input{border:1px;border-color:#000055; border-style:solid; font-weight:bold; 
background-color:#F0F0FF; font-family: Arial,seriv !important; width:277px; padding:5px; }
html tbody tr td p input{border:1px;border-color:#000055; border-style:solid; cursor:pointer; font-weight:bold; 
background-color:#F0F0FF; font-family: Arial,seriv !important; width:277px; padding:5px; margin:8px;}

html div.newstext form center input{border:1px;border-color:#000055; border-style:solid; cursor:pointer; 
font-weight:bold; background-color:#F0F0FF; font-family: Arial,seriv !important; width:277px; padding:5px; margin:8px;}

#match_all-yes,#match_all-no{border:1px;border-color:#000055; border-style:solid; cursor:pointer; font-weight:bold; 
background-color:#F0F0FF; font-family: Arial,seriv !important; width:40px; padding:1px; margin:1px;}


#search_forum option{border:1px;border-color:#000055; border-style:solid; cursor:pointer; font-weight:bold; 
background-color:#F0F0FF; font-family: Arial,seriv !important; width:260px;  padding:1px; margin:1px;}

#search_words, #search_in, #search_date, select{border:1px;border-color:#000055; border-style:solid; 
cursor:pointer; font-weight:bold; background-color:#F0F0FF; font-family: Arial,seriv !important; width:290px;  
padding:2px; margin-bottom:15px;}

blockquote pre {max-width:100px !important; font-family:Courier !important;}
}
