css:
@-moz-document domain("mlpchan.net") {

.body {
  margin-left: 1.5em;
}

a, a:visited {
  color: blue;
  text-decoration: none;

  transition: all .05s ease-in-out;
  -moz-transition: all .05s ease-in-out;
  -webkit-transition: all .05s ease-in-out;
}

.body a, .body a:visited {
  color: blue !important;
}

a:hover {
  color: dodgerblue;
}

.body a:hover {
  color: dodgerblue !important;
}

p.intro span.name {
  color: #050 !important;
  font-weight: bold;
}

p.intro a.email {
  border-bottom: 1px dotted grey;
}

p.intro span.subject {
  color: #020 !important;
  font-size: 1.2em;
  margin-right: 0.5em;
}

.image-hover {
  background-color: #eef2ff;
  margin: 0px !important;
  padding: 0px !important;
}

.blotter {
  color: black !important;
}

.fileinfo {
  font-size: 12px !important;
}

.boardlist.top {
  margin: 0 -13px !important;
  box-shadow: 0 -1px 8px black;
  background: #222;
}

.boardlist.top .boardlistpart {
  font-size: 0;
  padding-left: 5px;
}

.boardlist.top .boardlistpart a, .boardlist.top .boardlistpart a:visited  {
  display: inline-block !important;
  color: #ddd !important;
  padding: 8px 3px;
  margin: 0 1px;
  font-size: 16px;
}

.boardlist.top .boardlistpart a[href="/"] { 
  font-weight: bold; 
  color: #183 !important;
  padding: 8px 8px;
}

.boardlist.top .boardlistpart a:hover { background: #444; }

.boardlist.top .boardlistpart a.boardlistactive {
  padding-top: 4px;
  border-top: 4px solid #444;
}

.boardlist.top .boardlistpart a:before { content: "/"; }
.boardlist.top .boardlistpart a:after  { content: "/"; }

.boardlist.top .boardlistpart a[href="/"]:before { content: ""; }
.boardlist.top .boardlistpart a[href="/"]:after  { content: ""; }

}
