css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com")
{
*{
 font-family:segoe ui !important;
}
body{
 font-size: 12px !important;
} 
/* text boxes */
p, #pageNav, .logoutButton input, #label_persistent, .login_form_label_field, #reg_pages_msg, .locales, .preview, #events_side_header_link, .friend, .auxiliary, .data, .friend_browser_page_name_box, .uiSearchInput input, .fbGroupSummary, .comment_link input, .liketext, .UIActionLinks, .connect_widget_vertical_center, #footerContainer a, .notesBlogText, .uiButton, .pagerpro, .profileEditText, .fsm, .ego_contact_importer, #ueumkb_7 {
font-size:12px !important;
}

/* home page */
.fbxWelcomeBoxName, .uiSideNav, .navMoreLess, #birthday_warning, .UIImageBlock_Content, .commentBox textarea, .uiMorePager, .uiAttachmentTitle, .uiAttachmentDesc, .uiHeader h3, .uiHeader h4, .fbFeedbackContent .profileName {
font-size:12px !important;
}
.uiSideNav .item{
line-height: 16px !important;
}
.navHeader, .navHeader a, .countValue, .sortLink {
font-size: 11px !important;
}
/* share status, photos, etc. */
.uiComposerAttachments, .stat_elem {
font-size:12px !important;
}

/* notifications area */
.jewelCount span, .numMessages {
font-size: 10px !important;
}
.jewelHeader h3, .jewelFooter, .UIBeep_Title, .uiHeaderActions:not(.fsl), .notifMainLink {
font-size:12px !important;
}
.empty {
font-size: 12px !important;
}

/* dialog boxes */
.confirmation_message, .dialog_body {
font-size:12px !important;
}

/* messages */
.snippet {
font-size:12px !important;
}

.label, .fbChatSidebarBody li, .message, .name, .titlebarText {
font-size: 12px !important
}

.fbChatOrderedList .separator .text {
font-size: 11px !important;
}
/* profiles */
.fbProfileByline, .edit_profilepicture, .relationshipSection, .profile-friends-footer {
font-size: 12px !important;
}
/* timeline shiet*/
.fbTimelineSummarySection, .text, #ufbmou_2, .friendsBoxHeader, .friendSubtitle, .attachmentUnit, .fbTimelineScrubber {
font-size:12px !important
}

.fbTimelineUnitActor, .unitHeader, .fbTimelineAboutMeHeader h3 {
font-size: 13px !important;
}

.fbTimelineTabHeader h3 {
font-size: 16px !important;
}

/* photos */
.fbPhotoCaption, .photo_tag_name_frame, #phototags, .single_photo_header h4, #photoinalbum, #photoactions, .photoDetails, .fbPhotosTaglist, .detailsContainer, .photoDetailsContainer, .tagName {
font-size: 12px !important;
}
}
