css:
/*
* Ev-dragon.com - dark tchat
*
* Auteur  : Ihk
* Mise à jour : Juillet 2012
* Description : Modification de la page de tchate sur le site Ev-dragon (http://www.ev-dragon.com/)
* Usage  : A utiliser avec l'extension Stylish pour Firefox (http://userstyles.org/), ou par copie dans le fichier "userContent.css" de Firefox
*/

@namespace url(http://www.w3.org/1999/xhtml);


/* Page de Tchat
-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.*/
@-moz-document url("http://www.ev-dragon.com/tchate.php"), url("http://www.ev-dragon.com/tchate.php?taille=2"), url("http://www.ev-dragon.com/tchate.php?taille=1") {

small,p {
	display: none !important }
td {
    vertical-align: top !important }
iframe {
    height: 800px !important;
    border: 2px groove rgb(0, 0, 0) !important }
body {
	background-color: #222222 !important;
	color: #888888 !important;
	background-image: url('http://img651.imageshack.us/img651/7572/boisred.jpg') !important } 
div [id="messages"] {
	height: 100% !important;
	background: rgba(69, 69, 69, 0.8) !important;
	border: 2px groove rgb(0, 0, 0) !important}
div [id="premsg"] {
	width: 11.5% !important }
div [id="message2"] {
	width: 75% !important }
a {
	color: #BCBCBC !important;
	font-size: 10.5pt !important }
a:hover {
	color: #D9D9D9 !important }
body [color="#800000"] {
	color: #3189D7 !important }
body [color="#808080"] {
	color: #D9D9D9 !important }
body [color="#800080"] {
	color: #C15DC1 !important }
body [color="#000080"] {
	color: #6767FF !important }
body [color="#008000"] {
	color: #2DA32B !important }
span {
    margin:0.8% !important }
span[title~="Pseudo"], [title~="Pseudo?"] , [title~="Pseudo!"], [title~="Pseudo."] {
	font-size: 15pt !important;
	font-weight: bold !important } 

html input[type="text"], html textarea {
	background-color: black ! important }
}



@-moz-document url("http://www.ev-dragon.com/tchate3.php") {

body {
	background-image: url('http://img651.imageshack.us/img651/7572/boisred.jpg') !important;
	background: rgba(69, 69, 69, 0.8) !important;
	color: #888888 !important }
}


/* Publicités
-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.*/
@-moz-document url("http://www.ev-dragon.com/tchate.php") {

div[id="advert"], *[src*="pagead2.googlesyndication.com/pagead"], *[src*="xiti.com"], *[src*="informatiquefrance.free.fr/sms"], [title~="www.google.com"], *[href*="www.allopass.com/check/red.php"], *[href*="composants.rentabiliweb.com/masterGoTo.php"], *[href*="nolife"] {
	display: none !important }
}
