css:
/*
* Ev-dragon.com - transparence [Beta]
*
* Auteur  : Ihk
* Mise à jour : Juillet 2012
* Description : ajout de transparence et suppression des publicités sur le site Ev-dragon (http://www.ev-dragon.com/)
* Usage  : A utiliser avec l'extension Stylish pour Firefox (http://userstyles.org/), ou par copie dans le fichier "userContent.css" de Firefox
*/

@namespace url(http://www.w3.org/1999/xhtml);



/* Général
-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.*/
@-moz-document
        url("http://www.ev-dragon.com/"),
        url-prefix("http://www.ev-dragon.com/index.php") {

.t_titre, .t_chemin, td [bgcolor="#e0b060"], div [class~=infomsg], td [class~=t_titre] {
	background-color: rgba(224, 176, 96, 0.9) !important }
.t_classique, [style="text-align: left;"], table [style="background-color: rgb(255, 208, 128);"],td [class=t_classique] {
	background-color: rgba(255, 208, 128, 0.9) !important }
[class="titre"], [class="relief"],[class="exclu"] {
    background-color: transparent !important }
}


/* Publicités
-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.-.*/
@-moz-document domain("ev-dragon.com") {

div[id="advert"], *[src*="pagead2.googlesyndication.com/pagead"], *[src*="xiti.com"], *[src*="informatiquefrance.free.fr/sms"], [title~="www.google.com"], *[href*="www.allopass.com/check/red.php"], *[href*="composants.rentabiliweb.com/masterGoTo.php"], *[href*="nolife"] {
	display: none !important }
}
