@-moz-document domain("songsterr.com") {
    body{
        /* This is an obscure method, I know.
        If you know how to handle it better, let me know. */
        margin-top: -27px !important;
    }
    .contents-container {
      background: white !important;
    }
    
    .contents-inner-container, #hd {
      background: url("http://i.imgur.com/b3t6i.jpg") no-repeat top center !important;
    height:140px !important;
    padding-top:0 !important;
    }
    /*.right-buttons{
    	display:none !important;
    }*/
    #logo{
    	opacity: 0.01 !important;
    	width: 150px !important;
    	height: 90px !important;
    	display: block !important;
    	padding:0 !important;
    	margin: 0px 0 0 20px !important;
    float:left !important;
    }
    
    .MoreLink{
    	font-size: 12px !important;
    	font-family: tahoma !important;
    }
    .inlineHeading{
    	font-family: tahoma !important;
    }
     
     .signInMenuButton, #AboutLinks>a, .topLinks>a {
    	font-weight: normal !important;
    	color: #ddd !important;
    	display: inline !important;
    
     }
     /*
     .signInMenuButton:after, #AboutLinks>a:after, .topLinks >a:after { 
    	content:" | " !important;
    	text-decoration: none !important;
    }*/
    
    .signInOutButton, .favoritesButton,.signInMenuButton{
    	background: none !important;
    	width: auto !important;
    	padding-left: 0 !important;
    	
    	margin-top: -1px;
    	text-decoration: underline !important;
    	text-shadow: none !important;
    }
    .signInOutButton:hover, .favoritesButton:hover{
    	text-decoration: none !important;
    }
    #TopRightMenu{
        padding-top: 6px !important;
    	width: auto !important;
    	display:inline !important;
    	padding-right: 45px !important;
    }
    #AboutLinks{
    	float:right !important;
    	padding-top: 15px !important;
    }
    #az {
        padding-top: 0px !important;
        margin-top: -6px !important;
    	margin-left: 15px !important;
    }
    
    #az a {
    	text-decoration: none !important;
    	font-weight: bold !important;
    	margin-right: 10px !important;
    	display: inline-block !important;
    }
    #navigation {
    	height: 30px !important;
    }
    .allTags{
    	margin-right: 330px !important;
    }
    
    #search{
    	position: relative !important;
    	top: 132px !important;
    	left: 440px !important;
    
    }
    #searchForm > input.searchPattern{
    	background:white !important;
    	border: 1px solid #999 !important;
    	height: 16px !important;
    	font-size: 8pt !important;
    	color: #999 !important;
    	padding: 1px !important;
    	width: 160px !important;
    	font-weight:normal !important;
    }
    .submit.button, .right-buttons{
        float: right !important;
        position:absolute !important;
        top: 80px !important;
        
    }
    .submit.button{
        right:45px;
    }
    .right-buttons{
       right:144px;
    }
    #searchButton{
    	margin-left: 10px !important;
    	width: 75px !important;
    	height: 20px !important;
    	font-size: 9pt !important;
    	border-radius: 10px !important;
    	/* awesome tool: http://www.colorzilla.com/gradient-editor/ */
    	background: linear-gradient(to bottom, #a5a5a5 0%,#939393 6%,#939393 14%,#535353 48%,#000000 55%,#424242 66%,#666666 81%,#232323 97%) !important;
    }
    .collaboration-intro{
        display:none !important;
    }
    /* The most !important thing is to get a break sometimes.
    That was a bit tiring, pffff.... */
}
