css:
@-moz-document url-prefix("http://tracker.cztorrent.net/torrents") {
#banner_header { height: 1px !important; margin: 0 !important; }
div#reklama, div#reklama_special, a#hracky { display: none !important; }
a#ebony { height: 1px; width: 1px; margin-bottom: 0px; }
div.padding { padding: 20px 17px 0px 5px; margin: -18px; }
#content { padding: 0 20px 0 0; }
#conteiner { padding: 0px 20px 0 20px; }
#header { height: 46px; width: 870px; }
#top_menu { background-image: none; background-color: transparent; }
#header a:hover { background-position: 0px 80px !important; }
#users_stats #user_panel { height: 74px; }
#users_stats #user_panel dl { margin: 0; }
#caution { margin: -20px 0 0 0; }
#header a { z-index: 0; }
#hledat { padding: 4px 0px 2px 0px; margin-bottom: -32px; }
div.mark_new { position: absolute; top: 97px; left: 659px; }
p.pager { padding: 0; }
div.headline { height: 14px; padding-top: 0; }
div.block { width: 868px; padding-bottom: 3px; margin-bottom: 15px;}
#torrenty tr.torr_hover { height: 30px; }
#torrenty td.detaily { padding: 2px; }
#torrenty td.torr_new_act { height: 35px; }
#torrent_search { top: -20px; }
.input_hledat_cz { position: absolute; }
.user_menu_bottom { margin: 10px -20px -10px 0px; }
#bottom_menu { width: 852px; }
#footer { width: 842px; }
#footer_shadow { width: 882px; height: 30px; background-size: 882px; }
div.header_cz { background-size: 910px 100px; background-position-y: -20px; }
div.fce_panel { margin: -10px -20px 15px 0px; }
}
