css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("geekhack.org") {

    .subject p,
    .poster .smalltext,
    .postarea .flow_hidden,
    .reportlinks
    {
        display: none;
    }
    
    .attachments
    {
        border: 0 !important;
    }
    
    .poster
    {
        text-align: right;
        float: left !important;
    }
    
    .post_wrapper, .postarea
    {
        float: none !important;
        padding: 0 !important;
    }
    
    #forumposts .windowbg, #forumposts .windowbg2
    {
        border-top: 1px dotted gray;
        padding: 0 !important;
        margin: 0 !important;
        line-height: 100%;
    }
    
    #forumposts #new
    {
        display: block;
        width: 100%;
        border-top: 3px double darkred !important;
    }
    #new:hover
    {
        cursor: default !important;
    }
    
    img
    {
        max-width: 100%;
        height: auto !important;
    }
    
    .inner
    {
        border-top: 0 !important;
        padding: 0 !important;
    }
    
    /* quotes */
    blockquote
    {
        font-size: 100% !important;
        border: 0 !important;
    }

}
