@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*Identity Box icon label*/
#identity-icon-label, 
/*Identity Box icon country label*/
#identity-icon-country-label {
display:none !important;
}

/*Text*/
#identity-box:after {
font-size: 12px !important; 
display: -moz-box; -moz-box-align: center; 
padding-right: 4px !important;
}


/*HTTP*/
#page-proxy-favicon {
  list-style-image: url(data:image/png;base64,)!important;
}

/*HTTPS Secure*/
.verifiedDomain #page-proxy-favicon {
  list-style-image: url(data:image/png;base64,)!important;
}
/*#urlbar #identity-box.verifiedDomain:after {content:"Secure"; color: #993333; margin-left:0px !important;}*/

/*HTTPS Trusted*/
.verifiedIdentity #page-proxy-favicon {
  list-style-image: url(data:image/png;base64,) !important;
}
#urlbar #identity-box.verifiedIdentity:after {content:"Trusted"; color: #009900; margin-left:-7px !important;}

/*HTTPS mixed content*/
#identity-box.mixedContent #page-proxy-favicon  {
  list-style-image: url(data:image/png;base64,)!important;
}
