     html, body {
	background: #253960 url("data:image/png;base64,") top left repeat-x !important;
         background-repeat: repeat-x ! important; 
         color: lightgray ! important;}

     p, h1, h2, h3, h4 {
         background-color:transparent ! important; 
         color:lightgray ! important;}
     A:link, a, a.ab, .q {
         color:#82BDFF ! important; 
         text-decoration: none ! important;}
     A:visited {
         color: #4CB1FE ! important;}
     A:active {
         color:red ! important; 
         font-style: italic ! important;}
     A.good-rank:link {
         color: #00CC00 !important;}
     A.medium-rank:link {
         color: #ffee55 !important;}
     A.poor-rank:link {
         color: #ff8000 !important;
        opacity: 1 ! important;}
     A.unranked:link {
         color: #ff0000 !important;}
     A:hover {
         color:#ffffff ! important;}

     #header-bar {
         background:transparent ! important;
         border: 0px ! important;}

     #Header {
         background: #292929 ! important;
         border: 0px ! important;
         border-bottom: 1px solid #ffffff !important;
         border-top: 1px solid #ffffff !important;}

     #navigation a, #navigation a:visited {
         background: transparent ! important;
         border: 0px ! important;
         color: silver ! important;}

     #navigation .current-page{
         background: #353535 ! important;
         border: 0px ! important;
         color: white ! important;}
   
     #search input[type="submit"] {
         border: 0px solid #44a ! important;
         background: transparent ! important;
         color: orange ! important;}

     pre {
         background-color: transparent ! important;
         color: #ccb7b7 ! important;
         border: 1px dashed #cccccc !important;}

     textarea,input[id="short-description"],input[name="style[before_screenshot]"],input[name="style[after_screenshot]"] {
         background-color: #2b2b2b ! important;
         color: #ffffff ! important;
         border: 1px dashed #cccccc !important;}

     input[id="long-description"] {
         height: 50px ! important;}

     #obsoletion-message {
         background-color: transparent ! important;
         border: 3px solid #3c3c3c ! important;}

     #Comments, .CommentBody {
         color: lightgray ! important;
         border: 3px solid #3c3c3c ! important;}

     #Comments code,.CommentBody blockquote {
         background-color: lightgray ! important;
         color: red ! important;
         border: 3px solid #3c3c3c ! important;}

     div#Panel {
         background: transparent !important;}
