css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.amara.org") {

.unisubs-modal-widget {
	width: 1100px ! important;
}

[id$='_api']{
height:225px!important;
}
.unisubs-videoplayer{
height:225px!important;
}
.unisubs-captionSpan{
top:160px!important;
}

.unisubs-left {
width: 658px!important;
}

.unisubs-left div {

}

.unisubs-right {
height: 620px!important;
}

.unisubs-translateField {
width: 99%!important; 
height: 17px!important;
margin-top:0px!important;
margin-bottom:3px!important;
padding:2px!important;
}

.unisubs-translateField.unisubs-descriptionField{
height: 125px !important;
}

.unisubs-title {
padding-top: 3px!important; 
padding-bottom: 3px!important;
color:red!important;
}

.unisubs-title-notime
{
padding-top: 3px!important; 
padding-bottom: 3px!important;
color:red!important;
}

.unisubs-captioningArea {
height: 364px!important;
}

.unisubs-titlesList {
height: 360px!important;
}

.unisubs-titlesList li {
padding-top: 2px!important;
padding-bottom: 2px!important;
}

.unisubs-extra{
    margin: -98px 20px 20px 20px !important;
    padding: 18px!important
}

.unisubs-review-notes{
    height: 320px;
    width: 95%!important;
    margin: -30px 0px 0px 0px!important;
    Font: 12px Helvetica!important;
}

a.unisubs-download-subs {
    float: left !important;
    font-size: 11px !important;
    line-height: 2em !important;
    margin: 0px 0px 0px 20px !important;
    padding: 4px 10px 2px 10px !important;
    text-decoration: none !important;
}

.unisubs-done {
    clear: none !important;
    display: block !important;
    float: right !important;
    text-align: center !important;
    width: auto !important;
    padding: 5px 10px 5px 20px!important;
    margin: 0px 3px 0px 0px !important;
    font: 12px Helvetica!important;
}

.unisubs-saveandexit{
    visibility:hidden;
}

.unisubs-saveandexit a{
    font: 18px Helvetica!important;
    visibility: visible;
}
.unisubs-steps{
    clear:none!important;
    width:auto!important;
    padding:0px 20px 0px 0px!important;
}

.unisubs-backTo {
    margin:0px 0px 0px 10px!important;
    padding-right:14px!important;
}

.widget-button{
    padding:6px!important;
    margin-right:10px!important;
}

}
