css:
/* By VADemon v1.1 23.07.2012 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.lua.org/cgi-bin/demo"), url-prefix("http://www.lua.org/demo.html") {
html, body, .global_layout_wrapper.global_layout_onecol
{ 
background: black !important;  /* background */
color: white !important; /* Text color */
}
h2 { background-color: #1E0040 !important; } /* "Output" line */

/* textarea color, gray */
textarea {
  color : #323232 !important;
}
/* Replace Lua image */
img[alt="Lua"] {
height: 0 !important; /* remove */
width: 0 !important; /* the original image */
padding-left: 182px !important; /* add custom */
padding-top: 182px !important; /* image */
background-image: url("http://img33.imageshack.us/img33/4118/luastylish.png") !important;
}

/* remove that ugly panel when the mouse is over the Lua image */
a[href="home.html"] {
	background-color: transparent !important;
}

/* change links and all their children */
a, a * {
color: #089CDE !important;
text-decoration: none !important;
}

/* change visited links and all their children */
a:visited, a:visited * {
color: #001E70 !important;
}

/* change hovered links and all their children */
a:hover, a:hover * {
color: #089CDE !important;
}

} /* END */
