css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.wykop.pl/mikroblog'), url-prefix('http://www.wykop.pl/wpis'), url-prefix('http://www.wykop.pl/ludzie'), url-prefix('http://www.wykop.pl/moj'), url-prefix('http://www.wykop.pl/szukaj'), url-prefix('http://www.wykop.pl/dodatki/pokaz/'), url-prefix('http://www.wykop.pl/tag/') {
    div.brededed {
        border: none!important;
    }
    .activitiesStream .bgfa, .activitiesStream .bgf3f3f3 {
        background:transparent!important;
    }
    .activitiesStream blockquote p {
        padding:6px 0 !important;
        font-size:13px !important;
    }
    .activitiesStream blockquote header {
        background:#fafafa!important;
        border-left:0!important;
        font-size:12px !Important;
        padding:3px 3px 3px 15px!important;
        margin-left:-15px!important;
        border:1px solid #f6f6f6!important;
    }
    .activitiesStream blockquote header time {
        font-size:9px !Important;
    }
    .nightmode .activitiesStream blockquote header {
        border:0!important;
        border-bottom: 1px solid rgb(51, 51, 51)!important;
        background:transparent!important;
    }
    .activitiesStream .subcomments blockquote header {
        margin-left:-13px!important;
    }
    .activitiesStream .dropshadow {
        box-shadow:none !important;
    }
    .activitiesStream .votes {
        padding:0!important;
    }
/*[[avatar]]*/
