/*[[tagi]]*/
}

style_setting_options.value:
.votLiC, .votLiC a {
    color:rgba(128,128,128,0.2)!important;
    transition:0.2s;
}
blockquote:hover .votLiC, blockquote:hover .votLiC a {
    color:#777!important;
}

.newtag {
    background:transparent!important;
    border:0!important;
    box-shadow:none!important;
    text-shadow:none!important;
}

.newtag span:first-child {
    padding-right:0!important;
}

.newtag .number {
    border:0!important;
    padding-left:0!important;
    opacity:0.3!important;
}

.newtag:hover .number {
    opacity:1!important;
    transition:0.2s;
}
