css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wykop.pl") {
a:hover, a.tdnone:hover .hvline, .link, .link:visited {
    text-decoration: none !important;
}
.nightmode .newmarket {
    box-shadow:inset 0 0 1px rgba(0,0,0,1)!important;
    border:0!important;
    border-radius:3px !important;
    overflow:hidden!important;
}
.nightmode .newmarket .bgf6 {
    background:#292929 !important;
}
.nightmode .pager {
    margin-top:0!important;
    padding-top:36px !important;
    border-bottom:0!important;
}
.nightmode .brbottd5e4ed, .nightmode .brtopdashf1, .nightmode .brbottbd, .nightmode a:hover img.border,.nightmode .brtope8,.nightmode .brbottdf,.nightmode .brbotte8,.nightmode .button,.nightmode .overlay .bgfff,.nightmode .brbottcd,.nightmode .icon.minus.selected,.nightmode .icon.plus.selected,.nightmode .icon.minus.disabled,.nightmode .icon.plus.disabled,.nightmode table.default.data tbody td,.nightmode table.default.data tbody th,.nightmode img.border,.nightmode .slideselect ul.abs,.nightmode .slidebutton ul.abs {
    border-color:rgba(0,0,0,1) !important;
    box-shadow:0 1px 0 rgba(255,255,255,0.1)!important;
}
.nightmode .inner h3 {
    padding-bottom:12px!important;
    border-bottom:1px solid rgba(0,0,0,1)!important;
    box-shadow:0 1px 0 rgba(255,255,255,0.1)!important;
}
.nightmode #userPanel {
    border-bottom:1px solid rgba(0,0,0,1) !important;
    border-color:rgba(0,0,0,1)!important;
    box-shadow:0 1px 0 rgba(255,255,255,0.1)!important;
}
.nightmode .brtope8 {
    box-shadow:0 -1px 0 rgba(0,0,0,1) !important;
}
.nightmode .brtope8, .brbottdf, .brbotte8, .button, .overlay .bgfff, .brbottcd, .icon.minus.selected, .icon.plus.selected, .icon.minus.disabled, .icon.plus.disabled, table.default.data tbody td, table.default.data tbody th, img.border, .slideselect ul.abs, .slidebutton ul.abs {
    border-color: rgb(0, 0, 0);
}
.nightmode #microblogForm {
    margin-bottom:5px!important;
}
.nightmode .brc8 {
    border-color:#000!important;
}
.nightmode .subcomments li.show-all {
    border-top: 1px solid rgba(255,255,255,0.1)!important;
}
.nightmode #footer-con {
    background:#121212!important;
}
.nightmode a, .nightmode .link > * {
    color:rgba(255,255,255,0.8)!important;
}
.nightmode .streammini p a {
    text-decoration:underline!important;
}
.nightmode h1,.nightmode h2,.nightmode h3,.nightmode h4,.nightmode h5,.nightmode h6, .nightmode h1 span,.nightmode h2 span,.nightmode h3 span,.nightmode h4 span,.nightmode h5 span,.nightmode h6 span {
    text-shadow:0 1px 0 #000!important;
    color:/*[[color]]*/!important;
    font-weight:bold!important;
}
.nightmode #userpanel {
    margin-top:12px!important;
}
.nightmode aside h3 {
    margin-top:12px!important;
}
.nightmode ul.slidemenu.fright.x-normal {
    margin-top:12px!important;
}
.nightmode a:hover, .nightmode a .gray:hover, .nightmode a.link:hover > *, .nightmode a.tdnone:hover .link > *, .nightmode .slidemenu ul.abs a:hover {
    color:/*[[color]]*/!important;
}
.nightmode div.content a.block.ce1 {
    text-shadow:0 -1px 0 #000!important;
}
.nightmode .block.action {
    border:0!important;
    box-shadow:inset 0 0 1px #000;
    background:#121212!important;
}
.nightmode .brtope8 {
    border-top:1px solid rgba(255,255,255,0.1)!important;
    box-shadow:0 -1px 0 rgba(0,0,0,1)!important;
}
.nightmode .box {
    border:1px solid #000!important;
    box-shadow:0 1px 0 rgba(255,255,255,0.1)!important;
}
.nightmode .cff5917 {
    color:/*[[color]]*/!important;
}
.nightmode .comments header {
    text-shadow:0 1px 0 rgba(0,0,0,0.7)!important;
}
.nightmode .icon.plus:hover {
    border-color: rgb(145, 195, 156)!important;
}
.nightmode .icon.minus:hover {
    border-color: rgb(215, 148, 148)!important;
}
.nightmode nav.main .quickpoint.miniwall .count, .nightmode nav.main .quickpoint.miniwall:hover .count {
    background:/*[[color]]*/!important;
    box-shadow:none!important;
    font-weight:bold!important;
}

.nightmode h5 time, .nightmode h4 time {
    color: rgb(153, 153, 153) !important;
}
.nightmode .color-2, .nightmode .color-2:hover, .nightmode a.color-2, .nightmode a.color-2:hover  {
    color: rgb(255, 0, 0) !important;
}
.nightmode .color-1, .nightmode .color-1:hover, .nightmode a.color-1, .nightmode a.color-1:hover, .nightmode a.gray.color-1:hover,.nightmode a.gray.color-1:hover span {
    color: rgb(255, 89, 23)!important;
}
.nightmode .color-0, .nightmode .color-0:hover, .nightmode a.color-0, .nightmode a.color-0:hover, .nightmode a.gray.color-0:hover,.nightmode a.gray.color-0:hover span {
    color: rgb(51, 153, 51)!important;
}
.nightmode .content .color {
    border-bottom:1px dotted !important;
}
.nightmode ul.peoplewall > li {
    margin-bottom: 2px !important;
    margin-right: 2px !important;
}
.nightmode .digout {
    opacity:0.6 !important;
}
.nightmode .active a.tip {
    color:/*[[color]]*/!important;
    background:rgb(28, 28, 28)!important;
    border:1px solid rgb(28, 28, 28)!important;
}
.nightmode a.buttonnumber.dont {
    color:#222!important;
}
body.tag.nightmode .newtagheader {
    background:#111!important;
    border-bottom:1px solid rgb(28, 28, 28)!important;
}
}
