css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flickrhivemind.net") {
/* ==== Flickr WideScreen (Viewer) - FlickrHiveMind v.3 ==== */

/* HINT LINK RSS - === */
.nobox .smaller {
    position: absolute ! important;
    display: inline-block !important;
    height: 0px ! important;
    width: 1000px ! important;
    left: -885px ! important;
    padding: 0 ! important;
    top: -105px !important;
    font-size: 100% !important;
    text-align: left ! important;
    z-index: 0 !important;
}

/* EMPTY LINES -
.hivemind>br.hivemind>br{
    display: inline-block !important;
    line-height: 0 !important;
    height: 1px !important;
    width: 1000px !important;
    position: absolute !important;
    outline : red solid 1px !important; 
    margin-bottom: 0 !important;
    bottom: 0 !important;
    float: none !important;
    clear: both !important;
} === */
/* TAGCLOUD - TAGS FONT - === */
span.tagcloud0 {
    font-size: 11px !important;
}
span.tagcloud1 {
    font-size: 11px !important;
}
span.tagcloud2 {
    font-size: 12px !important;
}
span.tagcloud3 {
    font-size: 12px !important;
}
span.tagcloud4 {
    font-size: 13px !important;
}
span.tagcloud5 {
    font-size: 13px !important;
}
span.tagcloud6 {
    font-size: 13px !important;
}
span.tagcloud7 {
    font-size: 14px !important;
}
span.tagcloud8 {
    font-size: 14px !important;
}
span.tagcloud9 {
    font-size: 14px;
}
span.tagcloud10 {
    font-size: 15px !important;
}
span.tagcloud12 {
    font-size: 15px !important;
}
span.tagcloud13 {
    font-size: 15px !important;
}
span.tagcloud14 {
    font-size: 16px !important;
}
span.tagcloud15 {
    font-size: 16px !important;
}
span.tagcloud16 {
    font-size: 16px !important;
}
span.tagcloud17 {
    font-size: 17px !important;
}
span.tagcloud18 {
    font-size: 17px !important;
}
span.tagcloud19 {
    font-size: 17px !important;
}
span.tagcloud20 {
    font-size: 19px !important;
}
span.tagcloud21 {
    font-size: 19px !important;
}
span.tagcloud22 {
    font-size: 19px !important;
}
span.tagcloud23 {
    font-size: 22px !important;
}
span.tagcloud24 {
    font-size: 25px !important;
}

/* TGAS SELECTION - === */
/*.select>table>tbody {
    font-size: 10px ! important;
    line-height: 1em;
    width: 1900px !important;
    display: block !important;
    text-align: center !important;
}*/
.hivemind form div.form span.select {
    display: block !important;
    margin-left: 145px !important;
    margin-top: -2px !important;
}
.select>table>tbody>tr {
    display: inline !important;
    float: left !important;
    width: 620px !important;
    font-size: 10px ! important;;
    line-height: 1em;
}

/* SUPP(display none) - === */
.smalladvert ,
.leftsmalladvert ,
.motd_right ,
html body.hivemind form div.leftsmalladvertnb span.nobox div span.smaller em a.smaller {
    display: inline !important;
    display: none !important;
}

/* BUTTON - PAGINATOR/ SEARCH - CURSOR - === */

btn ,
.selectbtn{
    display: inline !important;
    cursor: pointer !important;
    font-size: 10px !important;
}
.btn:hover {
    background-color: RED !important;
}

/* SEARCH USER + BUTTONS "Recent/Intersting/ TimeLine" - === */
.nobox.centered.smaller {
    position: relative !important;
    display: inline-block !important;
    margin-top: -53px !important;
    left: -400px !important;
}
.quickbox.smaller.inline  {
    margin-top: -36px !important;
    text-align: center;
}

/* PAGINATOR BOTTOM - === */
div.inlinepager {
    float: none !important;
    display: block;
    height: 20px !important;
    line-height: 0;
    margin-bottom: 30px !important;
    padding: 5px 0 15px 0 !important;
    background: none repeat scroll 0 0 black;
    border: 1px solid gray !important;
    font-size: 100% !important;
    text-align: center !important;
}

/* TAGCLOUD - === */
#htmltagcloud {
    width: 98.7% ! important;
    margin-bottom: -20px !important;
    margin-left: 0px !important;
    line-height: 1;
    border: 1px solid gray !important;
    border-radius: 5px !important;
    padding: 10px !important;
    text-align: center;
    background: black !important;
}
/* TAGS - === */
span[class^="tagcloud"]:nth-child(even) a {
    color: #CED0D0 !important;
}
/* USERS - === */
.glink:nth-child(even) {
    color: #CED0D0 !important;
}
/* BLOCK TAGS + BLOCK CHOICES - === */
.leftnudge {
    position: relative ! important;
    width: 98.5% ! important;
    border: medium none;
    background: #333333 !important;
    margin-left: 5px ! important;
    margin-top: 7px !important;
}
/* BLOCK TAGS + INFOS USERS - === */
.leftnudge:first-of-type {
    position: relative ! important;
    width: 98.5% ! important;
    border: medium none;
    background: #333333 !important;
    margin-left: 5px ! important;
    margin-top: 7px !important;
    padding: 0.6em !important;
}
/* BLOCK CHOICES (Favorites / Groups, Interestingess ...etc ...) - === */
.leftnudge:first-of-type + .leftnudge {
    position: relative ! important;
    width: 99% ! important;
    border: medium none;
    background: #333333 !important;
    margin-left: 5px ! important;
    margin-top: 7px !important;
    padding: 0.3em !important;
}
/* BLOCK OPTIONS - "Enter Flickr User Name ... etc ..." - === */
.noborder {
    width: 98.5% ! important;
    padding: 5px ! important;
    height: 50px !important;
    line-height: 1em !important;
    margin-top: 1px !important;
    border: 1px solid gray !important;
}
/* BLOCK CHOICES ("Favorites/ Groups / Interestingness / Recent / Tags / User / Advenced") - 
POSITION HORIZONTAL - === */
.smaller.leftnudge.textcenter.softmargin .nobox {
    position: relative ! important;
    display: inline-block ! important;
    width: 0% ! important;
    margin-left: -1330px !important;
}

/* BLOCK TOPPAGER - BLOCK PREFERENCES - === */
.toppager {
position:absolute !important;
    float: left;
    clear: both;
    display: block;
    width: 50% !important;
    height: 60px !important;
    line-height: 3.5em !important;
    margin-bottom: 10px !important;
    margin-left: -1.5em !important;
    margin-right: 0.5em;
    margin-top: -5em !important;
    padding: 0.5em;
    background: black !important;
    border: thin solid #DDDDDD;
    z-index: 555000 !important;
}
div.toppager:before {
    content: "Select your Preferences : " !important;
    position:absolute !important;
    display: block;
    float: left;
    clear: both;
    width: auto !important;
    height: 15px !important;
    line-height: 1em !important;
    margin-bottom: 10px !important;
    margin-left: 10px !important;
    margin-right: 0.5em;
    margin-top: 10px !important;
    padding: 0.5em;
    border: thin solid #DDDDDD;
    background: black !important;
    z-index: 555000 !important;
}

/* BLOCK INFOS HITS + RSS -
div.leftsmalladvertnb {
    border: medium none;
    clear: both !important;
    display: block !important;
    float: none !important;
    margin-bottom: 10px;
    margin-left: 1251px !important;
    margin-right: 10px;
    margin-top: -60px !important;
    padding: 10px;
    position: relative !important;
} === */
div.leftsmalladvertnb {
    position: absolute !important;
    display: inline-block !important;
    float: right !important;
    height: 50px !important;
    width: 800px !important;
    left: 900px !important;
    top: 75px !important;
    padding: 0px !important;
    z-index: 55500 !important;
    text-align: center !important;
}

/* RSS LINK -
.nobox>div .smaller a[href$="=t"] {
    display: inline-block !important;
    position: absolute !important;
    position: absolute !important;
    outline : red solid 1px !important;
    float: right !important; 
    top: -10px !important;
} === */

/* HIVEMIND HITS LINKS - === */
.nobox .smaller[href]  {
    position: relative !important;
    display: inline-block !important;
    width: 100% !important;
    height:100% !important;
    margin-top: -200px !important;
    background: none repeat scroll 0 0 red !important;
    outline : red solid 1px !important; 
} 
/* TAGS BLOCK - === */
.biggerer {
    font-size: 100% !important;
}


/* TAGS - === */

[name="Tags"] +  .leftsmalladvert.biggerer.leftsmalladvert:before {
    content: "view TAGS" !important;
    float: left !important;
    width: 200px !important;
    margin-top: 125px !important;
    visibility: visible !important;
    border: 1px solid gray !important;
    border-radius: 5px !important;
    background: -moz-linear-gradient(center top , black, #4C4C4C) repeat scroll 0 0 transparent !important;
    text-align: center !important;
    cursor: pointer !important;
}
[name="Tags"] +  .leftsmalladvert.biggerer.leftsmalladvert:hover:before {
    float: left !important;
    visibility: visible !important;
    width: 200px !important;
    margin-left: -22px !important;
    margin-top: -10px !important;
    border: 1px solid gray !important;
    background: black !important;
}
[name="Tags"] +  .leftsmalladvert.biggerer.leftsmalladvert {
    display: block !important;
    float: left !important;
    visibility: hidden !important;
    height: 0px !important;
    width: 0px !important;
    margin-top: -100px !important;
    padding: 0 20px 0 20px !important;
    border: 1px solid gray !important;
}
[name="Tags"] +  .leftsmalladvert.biggerer.leftsmalladvert:hover {
    display: block !important;
    float: right !important;
    visibility: visible !important;
    height: 250% !important;
    width: 75% !important;
    margin-top: 35px !important;
    margin-right: 400px !important;
    background: black !important;
    border: 1px solid gray !important;
    z-index: 500 !important;
}

/* PAIRS - === */

[name="Pairs"] +  .leftsmalladvert.biggerer.leftsmalladvert:before {
    float: left !important;
    visibility: visible !important;
    content: "view PAIRS" !important;
    width: 200px !important;
    margin-top: 75px !important;
    margin-left: 280px !important;
    text-align: center !important;
    border: 1px solid gray !important;
    border-radius: 5px !important;
    background: -moz-linear-gradient(center top , black, #4C4C4C) repeat scroll 0 0 transparent !important;
    cursor: default !important;
}
[name="Pairs"] +  .leftsmalladvert.biggerer.leftsmalladvert:hover:before {
    float: left !important;
    visibility: visible !important;
    width: 200px !important;
    margin-left: 208px !important;
    margin-top: -25px !important;
    border: 1px solid gray !important;
    background: black !important;
}
[name="Pairs"] +  .leftsmalladvert.biggerer.leftsmalladvert {
    float: left !important;
    display: inline-block !important;
    visibility: hidden !important;
    height: 0px !important;
    width: 0px !important;
    margin-top: -50px !important;
    margin-left: -2.8% !important;
    padding: 0 20px 0 20px !important;
    z-index: 500 !important;
    border: 1px solid gray !important;
}
[name="Pairs"] +  .leftsmalladvert.biggerer.leftsmalladvert:hover {
    float: left !important;
    visibility: visible !important;
    height: 100% !important;
    width: 90% !important;
    margin-top: 50px !important;
    margin-left: 1% !important;
    z-index: 500 !important;
    background: black !important;
    border: 1px solid gray !important;
}

/* (new)TAGS - HOVER  - BACKGROUND - === */
#htmltagcloud a:hover ,
.glink:hover {
    background: #1C1919 !important;
    outline: 1px solid red !important;
}

/* USERS === */

[name="Users"] +  .leftsmalladvert.biggerer.leftsmalladvert:before {
    float: left !important;
    visibility: visible !important;
    content: "view USERS" !important;
    width: 200px !important;
    margin-top: 75px !important;
    margin-left: 500px !important;
    text-align: center !important;
    border: 1px solid gray !important;
    border-radius: 5px !important;
    background: -moz-linear-gradient(center top , black, #4C4C4C) repeat scroll 0 0 transparent !important;
    cursor: default !important;
}
[name="Users"] +  .leftsmalladvert.biggerer.leftsmalladvert:hover:before  {
    float: left !important;
    visibility: visible !important;
    width: 200px !important;
    margin-left: 428px !important;
    margin-top: -25px !important;
    border: 1px solid gray !important;
    background: black !important;
}
[name="Users"] +  .leftsmalladvert.biggerer.leftsmalladvert {
    float: left !important;
    display: inline-block !important;
    visibility: hidden !important;
    height: 0px !important;
    width: 0px !important;
    margin-top: -50px !important;
    margin-left: -2.8% !important;
    padding: 0 20px 0 20px !important;
    z-index: 500 !important;
    border: 1px solid gray !important;
}
[name="Users"] +  .leftsmalladvert.biggerer.leftsmalladvert:hover {
    float: left !important;
    visibility: visible !important;
    height: 100% !important;
    width: 90% !important;
    margin-top: 50px !important;
    margin-left: 1% !important;
    z-index: 500 !important;
    border: 1px solid gray !important;
    background: black !important;
}

/*.leftsmalladvert.biggerer.leftsmalladvert {
    display: inline-block !important;
    height: 0px !important;
    width: 0px !important;
    margin-bottom: -800px !important;
}*/

/* ============= end ========== */


}
