css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kickassunblock.net") , domain("proxykat.eu") , domain("kickass.to") , domain("kat.ph") , domain("kickassunblock.info") , domain("kickass.pw"){

.headmainpart a.logo {
    display: block!important;
    width: 132px!important;
    height: 95px!important;
    position: absolute!important;
    top: 4px!important;
    z-index: 10!important;
    left: 14px!important;
    margin: 0px!important;
    background: url("http://i58.tinypic.com/2d0c9yq.jpg") no-repeat scroll 0px 0px transparent!important;}

header #menu{ background-color:#222 !important; }

body { background-color:#111 !important; 
color:#fff !important }

.headmainpart,#navstyle,.headmenu,.tagcloud,.headmenu ul li {background:transparent!important;}

.header_right_part .search,tr.firstr th a{border-color:#000 !important;}

.headmenu ul li a,.search input,.plain, .plain a,
#navstyle ul li a, .bodySearch a ,.siteButton span,.data th,span,a,h1,h2, h3,h4,h5,.widgetSize{
text-shadow:none!important;
 color:#fff !important }

table.data tr.odd { background-color:#222 !important }

.ui-menu.ui-search-autocomplete{ border-color:#333 !important;
border-radius:3px ;
background: linear-gradient(to bottom, #444 0%,#111 100%) repeat scroll 0 0 transparent !important }

table.data tr.odd:hover, table.data tr:hover ,.ui-menu .ui-menu-item a:hover, .ui-menu .ui-menu-item a.ui-state-hover, .ui-menu .ui-menu-item a.ui-state-active {
background: linear-gradient(to bottom, rgba(0,0,0,.20), black)!important; }

.torrentFileList tr td,
.sliderbox ul li, #sidebar, .hideSidebar,
table.data tr td, table.data tr th { border-color:#333 !important }

.showmore, .commentsView { background-color:#222 !important; border-bottom-color:#333 !important }


.tagcloud a:hover ,.headmenu ul li a:hover {border-bottom:none!important;
text-shadow: 0px 0px 10px rgb(210,210,210)!important;}

TD.red.lasttd.center,.leechBlock ,.widgetLeech{
text-shadow: 0px 0px 7px rgb(255,10,10)!important;
    color: rgb(255, 255, 255) !important;}

strong.red{
text-shadow: 0px 0px 7px rgb(255,255,255)!important;
    color: rgb(0, 0, 0) !important;}

 TD.green.center,.seedBlock,.widgetSeed{
text-shadow: 0px 0px 7px rgb(0,180,0)!important;
    color: rgb(250, 250, 250) !important;}


/*visited link */
.torrentname a:visited { color:#fff!important }

.torrentname a { color:#fff !important }

.previewcomment { color:#333 !important }

.bbedit-toolbar { background-color:#eee !important; border-radius:3px }

.torrent_files .folder, .torrent_files .folderopen,
.vuze, .vuze:hover { background-color:#444 !important }

.torrent_files span.folder a, .torrent_files span.folderopen a { color:#aaa !important }

.torrentFileList tr:hover { background-color:#222 !important }

.torrentFileList tr.last .torTree,.accentbox,.commentbody,.letterssort,TD.forumGroupName.lightivorybg.thin.white.left.font12px.novertmarg.forumhideJS,.sharingWidget{ background-color:transparent !important }

#comarea,hr.tabsSeparator { background-color:#333 !important }

.commentowner,.letterssort{ border-color:#333 !important }

select,textarea,.textinput,#adcopy-outer input{border-color:#444!important}

.qrate { background:#333 none !important; border-radius:3px }

ul.tabNavigation li a.selectedTab, ul.tabNavigation li a.selectedTab:hover { background-color:#000 !important; color:#aaa !important }

.reportedMessage { background-color:#222 !important }

.siteButton, .icon16 ,.search,A.darkButton,A.selectedTab,INPUT.textinput{border:none!important;
background: linear-gradient(to bottom, #444 0%,#111 100%) repeat scroll 0 0 transparent !important }

.siteButton:hover,.icon16:hover,.search:hover,A.darkButton:hover,A.selectedTab:hover,INPUT.textinput:hover{border:none!important;
background: linear-gradient(to top, #444 0%,#111 100%) repeat scroll 0 0 transparent !important }

/*side menu hover*/
#navigation li a:hover,#navigation li a:active,.partner2Button,.sliderbox ul li:hover {background: linear-gradient(to bottom, #444 0%,#111 100%) repeat scroll 0 0 transparent !important 
}

#previewPopupContainer .prevAV,.sharingWidgetBox
{background: linear-gradient(to bottom, #444 0%,#111 100%) repeat scroll 0 0 transparent !important;
border-color:#222!important}

.advertising .legend {display:none!important}

#navigation li .dropdown,#navigation li .dropdown li a,.tooltip, .ui-tooltip, .ui-widget, .ui-corner-all, .ui-widget-content {
background-color: #222 !important;
border-bottom: transparent!important;}

}
