css:
@-moz-document url('https://www.tumblr.com/login'), url('https://www.tumblr.com/'), url('https://www.tumblr.com/register'), url('http://www.tumblr.com/inbox/'), url('http://www.tumblr.com/inbox'), url('http://www.tumblr.com/help'), url('https://www.tumblr.com/preferences'), url('https://www.tumblr.com/lookup'), url('http://www.tumblr.com/explore'), url('http://www.tumblr.com/following'), url('http://www.tumblr.com/likes'), url-prefix('http://www.tumblr.com/dashboard'), url-prefix('http://www.tumblr.com/new/'), url-prefix('http://www.tumblr.com/reblog/'), url-prefix('http://www.tumblr.com/blog/'), url-prefix('http://www.tumblr.com/tumblelog/'), url-prefix('http://www.tumblr.com/tagged/'), url-prefix('http://www.tumblr.com/spotlight/'), url-prefix('http://www.tumblr.com/edit/'), url-prefix('http://www.tumblr.com/activity/'), url-prefix('http://www.tumblr.com/liked/by/')  , url("https://www.tumblr.com/inbox/"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/"), url-prefix("https://www.tumblr.com/activity/"), url-prefix("https://www.tumblr.com/liked/by/") {

body {
background: url('http://i.imgur.com/t9FE2.jpg') #000000 repeat fixed !important;
}

div.l-content, #dashboard_index, #dashboard_inbox, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,#dashboard_post_queue, #dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_directory, #dashboard_lookup_contacts, #main_doc_page, #dashboard_settings, #dashboard_create_tumblelog, #dashboard_members, #main_tag, #main_meetups, #main_about, #main_explore, #dashboard_goodies, #main_developers, #main_themes, #dashboard_manage_themes, #main_edit_theme, #main_jobs, #main_policy, #dashboard_tumblelog_settings {
background:#000000 url('http://i.imgur.com/t9FE2.jpg') fixed !important;
}

#dashboard_index div.l-content, #dashboard_inbox div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_likes div.l-content, #dashboard_followers div.l-content, #dashboard_members div.l-content, #main_tag div.l-content, #dashboard_tumblelog_settings #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content { background:rgba(0,0,0,0.1) !important }
.selection_nipple {
border-bottom-color: rgba(0,0,0,0.1) !important;
}

.permalink {
background: none !important;
}

.avatar_frame {
background-color: rgba(255,255,255,0.1) !important; border-color: rgba(255,255,255,0.1) !important;
}

.notification {
background: rgba(0,0,0,0.1) !important;
border: rgba(255,255,255,0.1) !important;
background-image: none !important;
margin-bottom: 1px !important;
}
.notification a {
border-bottom: 1px solid rgba(0,0,0,0.1) !important;
}
.notification a:hover {
border-bottom: 1px solid rgba(0,0,0,0.3) !important;
}
.notification_type_icon {
display: none !important;
}

.nipple {
border-right-color: rgba(0,0,0,0.07) !important;
}
.controls_section li {
background: rgba(0,0,0,0.1) !important;
}
.with_subtitle small {
color: rgba(0,0,0,0.5) !important;
}
.recessed a {
color: rgba(255,255,255,0.7) !important;
}
.radar {
display: none !important;
}
* {
text-shadow: none !important;
}
h1, .title {
color: rgba(0,0,0,0.6) !important;
}
.no_posts_found, h1.dashboard_header {
color: rgba(255,255,255,0.9) !important;
}
.dashboard_options_form {
background: rgba(0,0,0,0.1) !important;
border: none !important;
color: rgba(255,255,255,0.7) !important;
}
.dashboard_options_form_disclaimer, .dashboard_options_form_disclaimer a {
color: rgba(255,255,255,0.9) !important;
}
.blue_note, .blue_note a {
background: rgba(0,0,0,0.2) !important;
color: rgba(0,0,0,0.6) !important;
}

#footer, #footer a {
border: none !important;
color: rgba(0,0,0,0.5) !important;
}
img[src*=""] {
opacity: 0;
visibility: hidden;
}

.new_post {
background: #ffffff none center center no-repeat;
}
.new_post_label {
-webkit-text-stroke:0 !important;
color: rgba(0,0,0,0.4) !important;
opacity:1 !important;
top: 0px !important;
padding-top: 80px !important;
}
.new_post_label:hover {
color: rgba(0,0,0,0.7) !important;
}

#pagination a {
background: rgba(0,0,0,0.4) !important;
color: rgba(255,255,255,0.7) !important;
}
#pagination a:hover {
color: rgba(255,255,255,0.9) !important;
}

#dashboard_controls a, #dashboard_controls .control {
color: rgba(255,255,255,0.7) !important;
}
.dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left, .dashboard_controls_arrow_right {
display: none !important;
}

.new_post_label .new_post_label_icon { background-image: url('http://i.imgur.com/gmg7UHw.png') !important;}

div#right_column, div#right_column:after { background: none !important;
}

a#open_blog_link {
color: rgba(255,255,255,.69) !important;
}

button#unread_tag_notice.pill.notice.unread_tag_notice {
color: rgba(255,255,255,.35) !important;
}

#posts .post .post_avatar.lighter_blue {
background: none !important;
}
#posts .post .post_avatar.flat {
border: none !important;
}

#posts .post .post_avatar {
-webkit-border-radius: 6px !important;
-moz-border-radius: 6px !important;
border-radius: 6px !important;
background: rgba(255, 255, 255, .1) !important;
}

}

@-moz-document url('http://www.tumblr.com/new/blog/'), url-prefix('http://www.tumblr.com/edit'), url-prefix('http://www.tumblr.com/reblog') , url("https://www.tumblr.com/new/blog/"), url-prefix("https://www.tumblr.com/edit"), url-prefix("https://www.tumblr.com/reblog") {
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: #fff url('http://assets.tumblr.com//images/content_bg.png') !important;
padding: 5px 30px !important;
margin: auto !important;
}
button#unread_tag_notice.pill.notice.unread_tag_notice {
color:
rgba(255,255,255,.35) !important;
}
}
