css:
@-moz-document domain(tumblr.com) {
ol#posts li.notification {

  background-color: #eecc77!important;

  border-top: 1px solid #000000!important;

  border-left: 1px solid #000000!important;

  border-bottom: 1px solid #000000!important;

  border-right: 1px solid #000000!important;

  color: #000000 !important;

  text-decoration: none;

}

#logo {
height: 0 !important; 
width: 0 !important; 
padding-left: 189px !important; 
padding-top: 126px !important; 
background: url(http://25.media.tumblr.com/tumblr_lv1u9rOgUF1r585cxo2_1280.png) no-repeat !important;
background-size: 75%;
}

body {

background-color: #FFFFFF;
background-image: url('http://24.media.tumblr.com/tumblr_li51y4xNTc1qb60neo1_1280.png');
background-size: contain;
background-position: right bottom;
background-attachment:fixed;
background-repeat:no-repeat;

}

#posts .notification a:not(.avatar_frame) {

color: rgba(170, 0, 51, 1);

font-weight: bold;

text-decoration: none;

border-bottom: 0px solid #ffffff;

-webkit-background-clip: padding-box;

}



#posts .notification blockquote {

  border-left: solid 3px #005682;

}



#posts .notification .avatar_frame {

background-color: #000000;

border: 1px solid #000000;

position: absolute;

top: -1px;

left: -55px;

overflow: hidden;

display: inline-block;

}






.post.is_mine.with_avatar.new_post {

  color: #ffffff;

  font-family:courier new, monospace;

  background: rgba(238, 0, 68,0.30) !important; 

}




/* img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"],

.new_post_label_icon {

    opacity: 0 !important;

}
.new_post_label{

font-size:0px !important;

}*/


/*#new_post{

  background-image: url(http://i40.tinypic.com/34xfjg0.png) !important; 

  background-repeat: no-repeat !important; 

}*/


#posts .post.new_post .new_post_label {

-webkit-font-smoothing: antialiased;

opacity: 1;

font-size: 14px;

font-weight: bold;

color: #000000;

text-decoration: none;

text-transform: uppercase;

text-align: center;

width: 74px;

position: absolute;

}




#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  background: rgba(17, 68, 136, .3) !important; 

}



.selection_nipple:not([class="selection_nipple white"]) {

border-bottom: solid 11px rgba(00,100,150, 0.35)  !important;

}



#content_top {

  width: 0px !important;

  height: 0px !important;




}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

background: none !important;

width: 0px !important;

height: 0px !important;

margin-top: 0px !important;

border-bottom: solid 11px rgba(00,00,00, 0.50) !important;

border-right: 11px solid transparent !important;

border-left: 11px solid transparent !important;

}




#content_bottom {

  width: 0px !important;

  height: 0px !important;









}





#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  padding-top: 25px !important; 

  padding-bottom: 25px !important; 

   -moz-border-radius: 20px !important; 

}





LABEL {

  color: rgba(0,0,0, 1.0) !important; 

}




#footer {

  display: none !important; 

}



.radar {

  display: none !important; 

}


.selected .following,

.selected .followers,

.selected .settings,

.selected .posts,

.selected .messages,

.selected .drafts,

.selected .queue,

.selected .likes  {

  color: rgba(0, 0, 0, 0.50) !important; 

  background: rgba(0, 0, 0, 0.0) !important; 

}





#new_post .arrow {

  opacity: .50 !important;

  margin-left: -1px !important; 

}
}
