@-moz-document domain("facebook.com") {

/*this is a hacked together version of many styles on userstyles.org. i just made many minor improvements, and added many new things*/

/*change this image to the background of your choosing*/
#facebook {background-image: url(http://i.imgur.com/y7wJ6.jpg)!important; background-attachment: fixed !important;}
#home_main .clearfix a {color: #666666!important}
body {background-color:transparent!important}

.photo_widget img {border: 5px solid #FFFFFF !important;}

#ad_0, #ad_1, #ad_2, #ad_3, #ad_4, 
#ad_5, #ad_6, #ad_7, #ad_8, #ad_9,
#ssponsor,
.advert,
.ad_body,
.ad_capsule,
.ad_media,
.comments_ad_image,
.sponsors,
.sponsor_absolute { display:none !important; }

#new_stuff_content,
.nextstep { display:none !important; }
#more_section { display:none !important; }
#expandable_more { display:block !important; }
#more_link { display:none !important; }
div#ssponsor { display:none !important; }

#navigator .main_set li a.edit_link {color:#bdbdbd !important;}

#navigator .secondary_set li a {color:#c7c7c7!important;}

#navigator .main_set li a.active, #navigator .main_set li a:hover {background:#999999 none repeat scroll 0% !important;}

h2 a {color: #333333 !important;}

#sidebar .divider_bar {display: none !important;}

#app_non_nav_list { margin-top: -10px !important; }

#userprofile {background-color: #FFFFFF !important;}

.holder:hover {background-color: #999999 !important;}

#userprofile a {color: #111111 !important;}

.link_title {color: #666666 !important;}

.link_title:hover {color: #FFFFFF !important;
background-color: #999999 !important;}

*[class*="flex_shut"] > *[class*="box_head clearfix"] {background-color: #CCCCCC !important;}

*[class*="flex_open"] > *[class*="box_head clearfix"] {background-color: #999999 !important;}

*[id*="title_app"] {color: #eeeeee !important;}

#sidebar a.go_home {background:#3B5998 url(data:image/gif;base64,%3D%3D) no-repeat scroll left top !important;}

#navigator {background:transparent url(data:image/gif;base64,R0lGODlhFQAXAIAAACAgIAAAACH5BAAAAAAALAAAAAAVABcAAAIUhI+py+0Po5y02ouz3rz7D4aiVQAAOw%3D%3D) repeat scroll 0% !important;}

#navigator .main_set li a.edit_link {color:#BDBDBD !important;}

#navigator .main_set li a.global_menu_arrow, #navigator .main_set li a.global_menu_arrow_active {background:transparent url(data:image/gif;base64,R0lGODlhIgAWALMPAJKSkt7e3t3d3YuLi4mJidfX19XV1ZmZmaSkpJGRkZeXl56enqCgoP///4ODg////yH5BAEAAA8ALAAAAAAiABYAAARm8MlJq3Q4642tp1yofaRokp95opUqsq3LwaC80ZN9448+TomGUHiIZRRD4WJGEQwFFk5gGAhVBsNBdAMYAqwVQ8PgCRUahVer4ShzCA2CusJIhRArnC/D6+3bPH99fj6Dgn2HgXsRADs%3D) no-repeat scroll 0px center !important;}

#navigator .secondary_set li a {color:#c7c7c7 !important;}

#navigator .main_set li a.active, #navigator .main_set li a:hover {background:#4a4a4a none repeat scroll 0% !important;}

h2 a {color: #4c618d !important;}

#sidebar .divider_bar {display: none;}

#app_non_nav_list { margin-top: -10px !important; }

#presence_bar, #presence_ui {background-image:none!important;}

#page_body {background-color:#ffffff!important;}

#buddy_list_tab.presence_bar_button, #chat, #presence_notifications_tab, #chat_status_control_tab {background-color:#f7f7f7;}

}
