css:
/* "FACEBOOK Paisley (fixed) + sticky top nav bar!" is based on the original Dark Style Release by ModStar, which was continued by VincentD and eventuelly completly fixed by Haiggoh. Credit for the static-top-bar hack go to Code.Red

*/



@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com") 


{#blueBar       { background-image: url(http://24.media.tumblr.com/tumblr_m7n4cgV47p1r06qfqo1_250.png);

	background-repeat: repeat-x;

}



#navItem_nf a.item {

	margin-right: px;

	background-color: rgba(91,3,63,.4) !important;

}



body.ego_page.home.hasLeftCol.fbx.safari4.Locale_en_US { background-image: url(http://24.media.tumblr.com/tumblr_m7n4cgV47p1r06qfqo1_250.png)}





#contentCol {background-image: url(http://static.tumblr.com/qwcwqom/DvHm7o606/do_face_tutututututtuutuuut.png);

	background-repeat: repeat-y;

	width: 810px;

	margin-top: -6px;

	border-left-style: solid;

	border-left-color: #29282a;

}







div#leftCol {

	color: #f5f5f5;

}



span.linkWrap {

	color: #c6c6c5;

	font-size: 11px;

	font-weight: bold;

}



div.uiHeader.uiHeaderWithImage.uiHeaderPage.fbx_stream_header {

	width: 480px;

}



body.ego_page.home.hasLeftCol.fbx.safari4.Locale_en_US {background-image: url(http://24.media.tumblr.com/tumblr_m7n4cgV47p1r06qfqo1_250.png);

	background-repeat: repeat !important;}



img.uiProfilePhoto.profilePic.uiProfilePhotoLarge.img {

	height: 40px;

	width: 40px;

	border: 5px double #fffeff;

}



#c4d530d621f8e38c23785095 div.UIImageBlock.clearfix.fbxWelcomeBox a.fbxWelcomeBoxBlock.UIImageBlock_Image.UIImageBlock_MED_Image img.img {

	width: 30px;

	height: 30px;

	margin-left: 15px;

	margin-top: 7px;

}





.profile .profile_color_bar  { background-color:#9fafc5 !important; }



/* navigation */



#pageLogo a    { background:url(http://www.meetingstreetmarket.com/1/FacebookPaisley.png) no-repeat scroll -21px -3px !important;

	margin-left: 4px;

	margin-top: 8px;

}

#pageLogo a:hover {background: url("http://www.meetingstreetmarket.com/1/FacebookPaisley.png") no-repeat scroll -20px -32px, rgba(10,10,10,.0) !important;margin-top: 8px;}



#headNavOut    { background:#8b8a8b !important; !important; }



#content       { background-image: url(http://24.media.tumblr.com/tumblr_m7n4cgV47p1r06qfqo1_250.png);

	background-repeat: repeat !important;

	font-family: Helvetica;

}

#headNav       { background-image: url(http://static.tumblr.com/qwcwqom/nCZm7o6cv/sub_nav_tutuutututuut.png);

	background-repeat: repeat-x;

	height: 40px;

	width: 794px;

	padding-top: 2px;

	padding-bottom: 3px;

	margin-left: 188px;

}



div#headNav.clearfix {

	border: 1px solid #181818;

}



#leftCol {

	background-color:#9fafc5;

	width: 175px;

	padding-left: 7px;

	margin-top: -5px;

}



/* Control Remarks Color */

#home_stream, .ufiItem, .mall_divider_text {background: rgba(151,193,235,.001) !important;

}



/* Remove Borders & Lines */

* { border-color: #232322 !important;}





/* main nav icons */ 

#jewelRequest { 

   background-image: url("http://www.meetingstreetmarket.com/1/FacebookPaisley.png") !important;

   background-position: -104px -107.5px !important;margin-top: 8px;

}

#jewelMail { 

  background-image: url("http://www.meetingstreetmarket.com/1/FacebookPaisley.png") !important;margin-top: 8px;

  background-position: -104px -75px !important;

}

#jewelNotif { 

  background-image: url("http://www.meetingstreetmarket.com/1/FacebookPaisley.png") !important;margin-top: 8px;

  background-position: -104px -139px !important;

}

#jewelCase .jewel  { border:none !important; }

.jewelToggler:hover { background-color: #888689; }





/* search */

#navSearch       { border-color: !important; }

#navSearch input { border-color: !important; }

#navAccountLink:hover { color: #c65b85; }





/* nav tabs */

#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#343434 !important; color: #000; }





/* boxes */

.lightblue_box  { background-color: #8b8c91; border-color !important; }

.commentable_item .ufi_section  { background-color: #858185; }

textarea, .inputtext, .inputpassword  { border-color:#abaaab!important; }





/* links */

a  

    { color: #8e2691;}

.uiLinkButton input  

    { color: #c438c7;}

.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link

    { color: #c438c7;}

button.as_link  

    { color: #c438c7;}





/* side nav */

.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color: #454646; }



.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color: #3e3f42; }

.friends_online_sidebar li.buddy_row a:hover  { background-color:#9c989b !important; }



.unread  { background-color:#cfcdce !important; }

.line  { color: #c65b85;

	background-color: #cedbe6;

}





/* pop up box  */

#jewelCase .jewelFooter a   { background-color:#6a6b6e !important; }

#jewelCase .jewelFooter .uiTextSubtitle  { color: #f90c6b;  }



#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color: #fffeff; }



#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#cfcece !important; border-color:#aeadad !important; }

#jewelCase .jewelBox  { background-color:#ecebeb !important; }







/* copy */

.uiHeader h2  { color: #7c747c;

	margin-top: -4px;

	font: 23px Helvetica;

}





/* graffiti */

.current_stuff td  { background-color:#e8e6e7 !important; border-color:#c2c2c2 !important; }





/* profile page */

.UIComposer .UIComposer_Box  { background-color:#e4e2e3 !important; border-color:#cecece !important; }

.profile .box .box_header  { background-color:#e4e2e3 !important; border-color:#cecece !important; }





/* profile tabs */

.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ECECEC !important; border-color:#8f8f8f !important; }

.profile .top_bar ul.tabs li a.tab_link  { background-color:#b1b0b0 !important; border-color:#8f8f8f !important; }

.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#403f3f !important; color: #ff78b0; border-color:#434243 !important; }





/* chat */

#chat_tab_bar .disabled .chat_window .chat_conv  { background-color: #7a7778;  }

#chat_tab_bar .chat_header  { background-color:#8d8d8d !important; border-color:#000000 !important; }

#chat_tab_bar .chat_header_name a  { color:#c65b85 !important; }

#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color: #fbfbfb; color:#383838!important; }

#presence_bar .presence_menu_opts h2 span  { background:#a4a3a3 !important; }

#presence_bar .presence_menu_opts h2  { border-color:#a8a8a8 !important; }

.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background: #e8e8e8; }



#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#979696 !important; border-color:#777777 !important; }

#presence .presence_bar_button .inner_button  { border-top:none !important; }

#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ecebeb !important; }



#presence .presence_menu_opts  { border-color:#9d9c9c !important;

	background-color: #e7e7e7;

}







/* buttons */

.UIButton_Blue  { background:#484848 !important; border-color:#9c9b9b !important; }

.UIComposer_InputArea  { border-color:#b3b2b2 !important; }

.uiButtonConfirm  { background:#515050 !important; border-color:#8e8e8e !important; }

#presence #buddy_list_tab #buddy_count strong  { color:#ecebeb !important; }











}
