css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home") {

html, #launcher, .spacer, #searchContainer, #snippetContainer, #topSection{background:#222222 url("http://media11.dropshots.com/photos/1027563/20120315/b_083432.jpg") fixed no-repeat right top!important; background-size:100% 100%!important;
border:transparent!important;
box-shadow:none!important;
border-top: none !important;
}

#newContentWrapper.run-hero-animation #main-phone-container,
#brandLogo,
#flame-wrap,
#aboutMozilla,
#launcher,
#snippets,
#searchEngineLogo,
#restorePreviousSessionSeparator
{ display: none !important; }

#searchText,
#searchSubmit

{ -moz-appearance: none !important; 
font-size: 18px !important;
color: black !important;
text-shadow: -2px 1px 4px #6B8190 !important; 
font-weight: bold !important;
font-family: Segoe UI, Tahoma, sans serif !important;
padding: 0px !important;
outline: 0 none !important;
border-radius: 2px !important;
border: 5px solid !important;
-moz-border-left-colors: #2E4049 #000000 !important;
-moz-border-top-colors: #000 #677B82 !important; 
-moz-border-bottom-colors: #263D47 #677b82  !important;
-moz-border-right-colors: #5A7BA7 #000000 !important;
box-shadow: 4px 4px 8px rgba(104,124,147, .4) !important; }

#searchText, #searchSubmit
{ background: -moz-radial-gradient(center top,#000000, transparent) !important; }

#searchText:hover, #searchText:focus
{ background: -moz-radial-gradient(center top,#82939A, rgba(86,97,102, .5), rgba(73,83,87, .1)) !important; }

.launchButton:hover, #searchSubmit:hover {background-image: -moz-linear-gradient(#000 50%, rgba(0,0,0,.5))!important;
box-shadow: inset 3px 3px 9px 3px #dcdcdc!important; opacity:1!important; -moz-transition-property:background-image, opacity, box-shadow!important; -moz-transition-duration:500ms!important; -moz-transition-timing-function:linear!important;}

}
