@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thepiratebay.se") {
html, div, body, .inputbox, #inp input {
    background: #2C2C2C !important;
}
h2, h3, th, .highlight {
    background: #333 !important;
    border: 0px !important;
}
tbody, thead, tdm, td {
    border: 0px !important;
}
a, label, strong, input, div, h2, pre, body, #recaptcha_instructions_image {
    color: #eee !important;
}
dt {
    color: #F0CFB3 !important;
}
.download a, #legal-threats a {
    color: green !important;
    text-decoration: underline !important;
}
font {
    color: #aaa !important
}
td {
    background: transparent !important;
}
.comment > a, .nfo > pre > a {
    color: steelblue !important;
    text-decoration: underline !important;
}
.doodle {
    width: 100% !important;
}
#about {
    width: 50% !important;
}
tr, .nfo, .post, .categoriesContainer dt, #searchcloud, #rulez, #comments dt, #comment-1, #comment-2, #comment-3, #comment-4, #comment-5, #comment-6, #comment-7, #comment-8, #comment-9, #comment-10, #comment-11, #comment-12, #comment-13, #comment-14, #comment-15, #comment-16, #comment-17, #comment-18, #comment-19, #comment-20, #comment-21, #comment-22, #comment-23, #comment-24, #comment-25 {
    background: #333 !important;
    border: 1px solid #555 !important;
    border-radius: 4px !important;
}
#main-content, #legal-threats {
    border: 1px solid #555 !important;
    border-radius: 4px !important;
}
input, .https_for, #about {
    border: 1px solid #555 !important;
    border-radius: 4px !important;
    padding: 0px !important;
}
img#recaptcha_reload, img.refreshBtn {
	height: 0px !important;
	padding-top: 18px !important;
	width: 25px !important;
	background-image: url(data:image/png;base64,) !important;
	opacity: 0.8;
}
img#recaptcha_switch_audio {
	height: 0px !important;
	padding-top: 15px !important;
	width: 25px !important;
	background-image: url(data:image/png;base64,) !important;
	opacity: 0.8;
}
img#recaptcha_whatsthis {
	height: 0px !important;
	padding-top: 16px !important;
	width: 25px !important;
	background-image: url(data:image/png;base64,) !important;
	opacity: 0.8;
}
input[type="submit"], .searchBox, .https_form {
	background: #444 !important;
	color: #ccc !important;
	border: 1px solid gray;
	-moz-appearance: none;
	-webkit-appearance: none;
	font-size: 9pt !important;
	border-radius: 4px !important;
}
input[type="checkbox"]{
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	background: rgba(0,0,0,.4) !important;
	border: 1px solid rgba(255,255,255,.3) !important;
	height: 10px !important;
	width: 10px !important;
	color: #eee !important;
}
}
