css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pcsx2.net") {
input, #ja-navhelper.wrap, .mw-body, textarea, #footer, .ja-usertools-layout, .noprint, .ja-box-usertools, .fileupload, .clickformore > td, tr {
    background: #2C2C2C !important;
    color: #eee !important;
}
.main-inner1.clearfix {
    border: 0px !important;
}
.ja-breadcrums {
    display: none !important;
}
.mw-content-ltr table tbody tr td table tbody tr td div h2, .catlinks {
    background: #333 !important;
}
.inner.clearfix, .childcontent, .ja-moduletable.moduletable_menu.clearfix, .ja-moduletable.moduletable.clearfix, .ja-moduletable.moduletabletranslate.clearfix {
    border: 1px solid #eee !important;
    background: #333 !important;
    border-radius: 8px !important;
    color: #eee !important;
}
a, .menu-title, .menu-desc, p, b, blockquote, li, h1, h2, h3, h4, h5, td, th, .mw-headline {
    color: #eee !important;
}
.ja-cpanel-action, .ja-cpanel-tools h3, .vectorTabs ul li, .mw-content-ltr table tbody tr td table tbody tr td div, input {
    background: #111 !important;
}
select {
	-moz-appearance: none;
	-webkit-appearance: none;
	font-size: 9pt ! important;
	color: #fff !important;
	background: #333 !important;
	border-color: gray !important;
	border: 1px solid;
}
input[type="checkbox"] {
	-moz-appearance: none !important;
	-webkit-appearance: none !important;
	background: rgba(0,0,0,.4) !important;
	border: 1px solid rgba(255,255,255,.3) !important;
	height: 10px !important;
	width: 10px !important;
	color: #eee !important;
}
input[type="submit"], input[type="button"], select {
	background: #444 !important;
	color: #aaa !important;
	border: 1px solid gray;
	-moz-appearance: none;
	-webkit-appearance: none;
	font-size: 9pt !important;
}
}
