css:
#header{background-color:rgb(46,67,94);
//border-bottom:6px solid #fff;
margin:auto;
width:968px;
  //position:fixed;
}
nav#mainMenu li {
margin-right:0px;
float: left;
position: relative;
}
.lay_wrap{margin:auto;background-color:rgb(255,255,255);width:968px;height:1150px;}
header {
width: 968px;
position: relative;
z-index: 999;
word-break: keep-all;
}

.mod_main {
max-width: 760px;
_width: 590px;
}

.lay_sidebar{height:100%;
  
  background-color:rgb(251,251,251);
  
  boder-bottom:2px solid #fcfcfc;

}

nav#mainMenu {
height: 30px;
  padding:4px;
border: 0px solid #B7C6D6;
box-shadow: 0 0px #8B99A8;
-moz-box-shadow: 0 0px #8B99A8;
-webkit-box-shadow: 0 0px #8B99A8;
}
nav#mainMenu ul {
width:960px;
height: 30px;
padding: 0px 0 0 100px;
border-bottom: 0px solid #D5D9DF;
background: url(http://qzs.qq.com/qzonestyle/qzone_mobile_v1/img/login_header.png) no-repeat;
background-position:4px 4px!important;
//background-repeat:no-repeat!important;
//background-attachment:fixed!important;
}
nav#mainMenu li.cur a {
height: 30px;
cursor: default;
color: black;
border: 0px solid #D5D9DF;
border-width: 0 0 0;
background: white;
border-radius: 0 0 0 0;
-moz-border-radius: 0px 0px 0 0;
-webkit-border-radius: 0 0 0 0;
}

.lay_main_wrap {
width: 78%;
margin-left: 0px;
float: left;
background: white;
}
.lay_main {
margin-left: 0px;

}

#ownerOperation {
background-color: #F2F4F7;
padding:10px;
zoom: 1;
height:60px;
}
#ownerOperation .mod_interact_main {
padding-left: 50px!important;
}

.text, textarea, .status_input {
overflow: hidden;
font-size: 14px;
width: 95%;
_width: 97%;
padding: 0 1%;
margin: 0;
font-family: Helvetica;
_height: 32px;
min-height: 32px;
line-height: 32px;
border: 1px solid #7E8AAD;
border-radius: 2px;
-webkit-border-radius: 2px;
-moz-border-radius: 2px;
-webkit-box-shadow: none;
}

.mod_interact_avatar {
position: absolute;
width: 45px;
height: 45px;
overflow: hidden;
left: 10px;
top: 13px;
}

.mod_interact_avatar img {
height: 45px;
width: 45px;
}


.feed .f_aside {
width: 45px;
height:45px;
margin-right: 10px;
margin-top: 3px;
}
.imgBlock .imgBlock_img {
display: block;
float: left;
width: 45px;
height:45px;
}

.feed .f_aside img {
width: 45px;
height: 45px;
vertical-align: top;
}

#footer {
clear: both;
height: 133px;
width: 968px;
line-height: 28px;
background-color: #2E435E;
color: #8F9FB8;
font-size: 12px;
overflow: hidden;
font-family: "Microsoft Yahei";
margin:auto;
}




ul.feed_list .mod_interact_main {
padding-right: 10px;
padding-left: 64px;
}

ul.feed_list li {
color: black;
font-size:13px;
}
