css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {
body {
    background: none center center #333 no-repeat fixed !important;
  }
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png');
padding: 20px;
-webkit-border-radius: 15px;
-moz-border-radius: 15px;
border-radius: 15px;
}

div.l-header .selection_nipple {
width: 0;
height: 0;
border-left: 12px solid transparent;
border-right: 12px solid transparent;
border-bottom: 12px solid #000;
opacity:0.6;
font-size: 0;
position: relative;
top: 4px;
vertical-align: top;
margin: 0 auto;
display: none;
}

#right_column .controls_section .recessed {
background-color: #000;
opacity:0.6;
-webkit-box-shadow: none;
-moz-box-shadow: none;
box-shadow: none;
-webkit-border-bottom-right-radius: 6px;
-webkit-border-bottom-left-radius: 6px;
-moz-border-radius-bottomright: 6px;
-moz-border-radius-bottomleft: 6px;
border-bottom-right-radius: 6px;
border-bottom-left-radius: 6px;
position: relative;

}

#right_column .controls_section .recessed:hover {
background-color: #000;
opacity:0.6;
-webkit-box-shadow: 0 0 5px #000;
-moz-box-shadow: 0 0 5px #000;
box-shadow: 0 0 5px #000;
}

#right_column .controls_section .selected {
background: #c31c16;
background: -moz-linear-gradient(top,#000 0,#000 50%,#000 50%,#000 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#000),color-stop(50%,#000),color-stop(50%,#000),color-stop(100%,#000));
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#000',endColorstr='#000',GradientType=0);
-webkit-box-shadow: 0 0 5px #000,inset 0 1px 0 rgba(255,255,255,.40);
-moz-box-shadow: 0 0 5px #000,inset 0 1px 0 rgba(255,255,255,.40);
box-shadow: 0 0 5px #000,inset 0 1px 0 rgba(255,255,255,.40);
-webkit-border-radius: 1px;
-moz-border-radius: 1px;
border-radius: 1px;
border-top: none;
}

#right_column .controls_section .selected:hover {
background: #c31c16;
background: -moz-linear-gradient(top,#000 0,#000 50%,#000 50%,#000 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#000),color-stop(50%,#000),color-stop(50%,#000),color-stop(100%,#000));
filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#c31c16',endColorstr='#6B903E',GradientType=0);
-webkit-box-shadow: 0 0 5px #000;
-moz-box-shadow: 0 0 5px #000;
box-shadow: 0 0 5px #000;

border-top: none;
}

#right_column .radar .content {
background: url('http://static.tumblr.com/l5qjkdi/lZBlnitgv/bg_body.png') repeat-y;
position: relative;
}

#right_column .radar .content.is_media .mask.top {
height: 9px;
background: none;
background-position: 0 0;
top: 0;
}

#right_column .radar .content.is_media .mask.bottom {
height: 19px;
background: none;
background-position: 0 -9px;
bottom: 0;
}

img#content_top, img#content_bottom {
display:none;
}
#right_column::after {
position: absolute;
top: 100%;
right: 0;
left: 0;
height: 35px;
content: ' ';
background: -moz-linear-gradient(top,rgba(0, 0, 0, 0) 0,rgba(0, 0, 0, 0) 100%);
background: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(0, 0, 0, 0)),color-stop(100%,rgba(0, 0, 0, 0)));
background: -webkit-linear-gradient(top,rgba(0, 0, 0, 0) 0,rgba(0, 0, 0, 0) 100%);

background: linear-gradient(top,rgba(0, 0, 0, 0) 0,rgba(0, 0, 0, 0) 100%);
}
#posts .post .permalink {
background: none #333;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;

}
#posts .notification.single_notification,#posts .notification.first_notification, #posts .notification, #posts .notification.last_notification {
background:none #333;
border-color: #000;}

#posts .notification.alt .nipple {
border-right: 6px solid #333;
}
#posts .notification .nipple.border {
border-top: 7px solid transparent;
border-bottom: 7px solid transparent;
border-right: 7px solid #000;
position: absolute;
left: -7px;
top: 9px;
}

#posts .notification .nipple {
width: 0;
height: 0;
border-top: 6px solid transparent;
border-bottom: 6px solid transparent;
border-right: 6px solid #333;
position: absolute;
left: -5px;
top: 10px;
}
#auto_pagination_loader {
font-size: 34px;
font-weight: bold;
color: white;
text-align: center;
text-shadow: #000 2px 2px 3px;
background:#333;
border-color:#000;
}
#posts .notification .avatar_frame {
background-color: #000;
border: 1px solid #000;
position: absolute;
top: -1px;
left: -55px;
overflow: hidden;
display: inline-block;
}
#posts .notification a:not(.avatar_frame) {
color: rgba(255, 255, 255, .56);
font-weight: bold;
text-decoration: none;
border-bottom: 1px solid rgba(255, 255, 255, .56);
-webkit-background-clip: padding-box;
}

.return_to_top_icon{content:""!important;background:rgba(90,90,90,.5)!important;border:solid thin rgba(0,0,0,1)!important;-moz-border-radius:8px!important;-webkit-border-radius:8px!important;border-radius:8px!important;}.return_to_top_icon:before{width:0px!important;position:absolute!important;content:""!important;border-right:solid 13px transparent!important;border-bottom:solid 13px rgba(0,0,0,1)!important;border-left:solid 13px transparent!important;margin-top:12px!important;margin-left:12px!important;}.return_to_top_icon:after{position:absolute!important;content:""!important;width:12px!important;height:13px!important;background-color:rgba(0,0,0,1)!important;margin-top:25px!important;margin-left:19px!important;}
#posts .notification.alt {
background-color: #333;
}
.edit_post_sharing .share_label {
position: relative;
display: block;
height: 42px;
width: 42px;
overflow: hidden;
text-indent: -999em;
border-radius: 4px;
box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.5),0 1px 0 rgba(255, 255, 255, 1);
border: 1px solid rgba(128, 128, 128, 1);
cursor: pointer;
background: rgba(128, 128, 128, 0.9);}

#posts .post .post_avatar.lighter_blue {
background-color: #333;
}
.dashboard_options_form, .tag_psa {

border: 1px solid #000;
}

#posts .post .post_avatar.flat {

border: 1px solid #000;
}

}
