css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ar.pycon.org") {
    #page {width: 100% !important; background: white !important;}
    #user_menu {display: none !important;}
    div#content {width: 90% !important;}
    table.schedule { font-family: ubuntu !important; font-size: 11pt !important}
	table.schedule, table.schedule th  {color: black !important;}
    table.schedule a, .frame a {color:#005 !important;}
    body, h1, h2, h3, p, .frame {color: #555 !important;}
    div#header-container {
        background: none !important;
        width: 100% !important;
        height: 0% !important;
    }
    div#header-container h1 {
        margin: 0px !important;
    }
    div#header_side {
        height: auto !important;
        width: auto !important;
        margin: 0px !important;
    }
    div#header_side span {
        float: none !important;
        width: auto !important;
    }
    div#header h1 a{
        text-indent: inherit !important;
        font-family: LullabyRegular;
        width: auto !important;
        height: auto !important;
        font-size: 40px;
        text-decoration: none !important;
    }
    div#header h1 {
        width: inherit !important;
        height: inherit !important;
        background: transparent !important;
        margin: 2px !important;
    }
    div#header_side br, div#footer, div#sidebar, div.idioma, div#header_side h2 {
        display: none !important;
        border: solid 1px blue !important;
    }
    div#menu {
        background: rgb(5, 91, 142) !important;
        width: 100% !important;
    }
}
