css:
@-moz-document domain("twocansandstring.com") {
div#top_panel, div#content_begin_with_nav { 
border-bottom-left-radius:0px !important; 
border-bottom-right-radius:0px !important; 
} 

div#dark_nav, div#footer_host { 
border-top-left-radius:0px !important; 
border-top-right-radius:0px !important; 
} 

div#content_begin_no_nav { 
border-radius:0px !important; 
} 

div[style] {
    border-radius:0px !important;
}

#content_host div[style="padding:8px;font-size:12px;text-align:center;margin:8px;margin-right:0px;width:136px;background-color:#eee;border-radius:8px;"] {
       background-color:#ddd !important;
       border: 1px solid #AAA !important;
       text-align:left !important;
}

#content_host td[style="padding-right:35px;"], #content_host td[width="136"] {
       background-color:#eee !important;
}

#content_host div[style="font-size:12px;width:100%;padding:8px;margin:8px;background-color:#fff;border-radius:8px"], #content_host div[style="border-radius:8px;background-color:#ddd; margin-bottom:20px;"] {
       border: 1px solid #AAA !important;
}

#content_host a[style="color:#888;"]{
       color:#333 !important;
}

#content_host div[style="font-size:12px;width:100%;padding:8px;margin:8px;margin-top:0px;background-color:#fff;border-bottom-left-radius:8px;border-bottom-right-radius:8px"], div[style="font-size:16px;width:100%;padding:8px;padding-top:1px;padding-bottom:1px;margin:8px;color:#fff;margin-bottom:0px;text-align:left;background-color:#404040;font-weight:bold;;border-top-left-radius:8px;;border-top-right-radius:8px;"], #content_host,  {
      border: 1px solid #888 !important; 
}

}
