css:
@-moz-document domain("twocansandstring.com") {
body {
	background-color:#1a1a1a;
        background-image:url('http://i.imgur.com/499RL.png');
	background-attachment: fixed;
        color:#999999;
	font-family: "Trebuchet MS", sans-serif;
        opacity:0.92 !important;
}

img { border: 0px; }

a:link, a:visited {
	color:#a00 !important;
}

a:hover {
	color:#f00 !important;
	text-decoration:none !important;
        text-shadow: 1px 1px 6px #ff0000;
	filter: dropshadow(color=#ff0000, offx=1, offy=1);
}

div#top_panel {
	background-image:none !important;
	background-color:#1e1e1e !important;
}

div#dark_nav {
	background-color:#343434 !important;
        color: #CC0000 !important;
        font-family: "Trebuchet MS", sans-serif !important;
}

div#content_host {
        color:#CC0000 !important;
        background-color:#1e1e1e !important;
}

h1,h2,h3,h4,h5,h6 {
        color: #CC0000 !important;
        font-family: "Copperplate Gothic Light", "Trebuchet MS", sans-serif !important;
}

#content_host div[style="text-align:left; margin-bottom:10px; background-color:#eee; border:1px solid #aaa; border-top-left-radius:6px; border-top-right-radius:6px;color:#000; padding:8px;"], div#footer_host {
        background-color:#343434 !important;
        border-bottom-width:1px !important;
}

#content_host div[style="padding:8px; background-color:#fff; border:1px solid #aaa; border-top-left-radius:6px; border-top-right-radius:6px;color:#000;"] {
       background-color:#767676 !important;
       color:#000 !important; 
}

#content_host div[style="font-style:italic; color:#888;"], #content_host div[style="color:#777; font-style:italic; padding:8px;border-bottom:1px solid #aaa;"], #content_host div[style="color:#777; font-style:italic; padding:8px;"] {
       color:#000 !important;
}

#content_host div[style="background-color:#fff; border:1px solid #aaa; border-top-left-radius:6px; border-top-right-radius:6px;color:#000;"] {
        background-color:#767676 !important;
}

#content_host span[style="font-weight:bold; color:#00f;"], #footer_host .num {
        color:#f00 !important;
}

div#top_panel, div#content_begin_with_nav { 
border-bottom-left-radius:0px !important; 
border-bottom-right-radius:0px !important; 
} 

div#dark_nav, div#footer_host { 
border-top-left-radius:0px !important; 
border-top-right-radius:0px !important; 
} 

div#content_begin_no_nav { 
border-radius:0px !important; 
} 

div[style] {
    border-radius:0px !important;
}



#title_host img[src="/images/twocans/title.png"] {
	height: 0 !important;
	width: 0 !important;
	padding-left: 207px !important;
	padding-top: 100px !important;
	background: url('http://i.imgur.com/MTTab.png') no-repeat !important;
}

#feature_host {
        color:#fff !important;
}

#feature_host span[style="color:#f00;"] {
        color:#333 !important;
        text-shadow:
        -1px -1px 0 #fff,
        1px -1px 0 #fff,
        -1px 1px 0 #fff,
        1px 1px 0 #fff !important;  
}

#footer_host {
        color:#767676 !important;
}

#starbucks {visibility: hidden;}

textarea, input[type="text"] {
border:1px solid #f00 !important;
background-color:#ccc !important;
}

}

@-moz-document url-prefix("http://twocansandstring.com/forum") {
#content_host div[style="padding:8px;font-size:12px;text-align:center;margin:8px;margin-right:0px;width:136px;background-color:#eee;border-radius:8px;"] {
       background-color:#999999 !important;
       border: 1px solid #AAA !important;
       text-align:left !important;
}

#content_host td[style="padding-right:35px;"], #content_host td[width="136"] {
       background-color:#343434 !important;
}

#content_host div[style="font-size:12px;width:100%;padding:8px;margin:8px;background-color:#fff;border-radius:8px"], #content_host div[style="border-radius:8px;background-color:#ddd; margin-bottom:20px;"], #content_host div[style="font-size:12px;width:100%;padding:8px;margin:8px;margin-top:0px;background-color:#fff;border-bottom-left-radius:8px;border-bottom-right-radius:8px"] {
       border: 1px solid #AAA !important;
       background-color:#767676 !important;
       color:#000 !important;
}

#content_host,  {
      border: 1px solid #888 !important;
}

#content_host div[style="font-size:16px;width:100%;padding:8px;padding-top:1px;padding-bottom:1px;margin:8px;color:#fff;margin-bottom:0px;text-align:left;background-color:#404040;font-weight:bold;;border-top-left-radius:8px;;border-top-right-radius:8px;"] {
        border: 1px solid #888 !important;
        background-color:#000 !important;
        color: #CC0000 !important;
        font-family: "Copperplate Gothic Light", "Trebuchet MS", sans-serif !important;
        padding-top:4px !important;
        padding-bottom:4px !important;
        border-bottom:0px !important;
}

#content_host tr[style="background-color:#eee;"] {
        background-color:#767676 !important;
        color:#000 !important;
}

#content_host tr[style="background-color:#d8d8d8;"], #content_host tr[style="background-color:#fff;"] {
        background-color:#999999 !important;
        color:#000 !important;
}

#content_host div[style="font-weight:bold; color:#07f;"] {
        Color:#f00 !important;
}

#content_host div[style="color:#88b;font-size:12px;"] {
        color:#000 !important;
}

#content_host tr[style="background-color:#c9cedf;font-weight:bold;"] {
        background-color:#aa7676 !important;
        color:#000 !important;
}

#content_host tr[style="background-color:#b9becf;font-weight:bold;"]
{
        background-color:#bb9999 !important;
        color:#000 !important;
}
}

@-moz-document url-prefix("http://twocansandstring.com/messages") {
#content_host div[style="border:1px solid #888; padding:10px; margin-top:8px;"] {
       color:#000 !important;
       background-color:#767676 !important;
       border:1px solid #ddd !important;
}

#content_host div[style="border:1px solid #888; padding:10px; margin-top:8px;"] [style="font-size:14px; font-weight:bold; margin-bottom:8px;"] {
       color:#000 !important;
}

#content_host tr[style="background-color:#eee;"] {
       background-color:#767676 !important;
}

#content_host tr[style="background-color:#fff;"] {
       background-color:#999999 !important;
}

#content_host div[style="font-size:11px; color:#888;"] {
       color:#000 !important;
       font-size:12px !important;
}

#content_host td {color:#000 !important;}

#content_host tr[style="background-color:#444; color:#fff; font-weight:bold; font-size:11px;"] td {
       color:#cc0000 !important;
       background-color:#343434 !important;
       font-size:14px !important;
}
}

@-moz-document url-prefix("http://twocansandstring.com/ask") {
#question_list div[style="border:1px solid #ddd; margin:8px; background-color:#ddd; position:relative;font-size:14px; font-weight:bold; padding:8px;"] {
       color:#000 !important;
       background-color:#343434 !important;
}

#question_list div[style="margin:8px; padding:10px;background-color:#ff0;"] {
       background-color:#ff5656 !important;
}

#question_list div[style="margin:8px; padding:10px;background-color:#fff;"] {
       background-color:#885656 !important;
}

#question_list div[style="margin-right:44px; background-color:#fff; padding:10px;"] {
       background-color:#767676 !important;
}

#question_list div[style="font-size:10px;background-color:#bbb;padding:4px;text-align:right;color:#000;"] {
       background-color:inherit !important;
       color:#767676 !important;
}

#question_list div[style="position:absolute; right:8px; top:8px; width:28px; padding:3px; border:1px solid #333; text-align:center;padding-top:6px;background-color:#dcc;"] {
       background-color:#cc0000 !important;
}
}

@-moz-document url-prefix("http://twocansandstring.com/things") {
#article_body {color:#ccc !important;}

#content_host div[style="padding:10px; border:1px solid #e2e2e2; color:#555; margin-bottom:10px;clear:left; font-size:13px; line-height:26px;"] {
              color:#ccc !important;
}
}
