  .textInfoView .textInfoOwnStatsNumber { color: #C8C8C8; }

  
  .textInfoView .textInfoOwnStatsNumber-good { color: #99CC00; }
  .textInfoView .textInfoOwnStatsNumber-bad  { color: #D08383; }

  
  .countdownPopup { background-color: #2B4866; }

  
