css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iichan.hk") {    
    
    /* General */
    
    body, textarea, input, table.category {
        font-family: 
            "Palatino", /* OS X */
            "Palatino Linotype", /* Windows */
            "Nimbus Roman No9 L", "URW Palladio L", /* Linux */
            serif !important;
        font-size: 11pt;
    }
    
    code {
        font-size: 10pt;
    }
    
    body {
 	    background: #ffe0e2 !important;
	    color: #442330 !important;
	}
	
	a {
	    color: #d30054 !important;
	    text-decoration: none !important;
	    font-weight: normal !important;
	}
	
	a:hover {
        color: #ff3887 !important;
    }
    
	blockquote {
	    margin: 8px;
	}
	
	form {
	    width: 832px;
	    margin: 0 auto;
	}
	
   	body > table, 
	body > table td
	{
	    border: none !important;
	}
		
	/* Link bar */
	
	.adminbar,
	.boardsbar {
        width: 828px;
        text-align: center !important;
        margin: .6em auto !important;
        float: none !important;
    }

	.adminbartable td {
	    display: block;
	}
	
	/* Banner & board title */
	
	.logo {
        color: #333 !important;
        width: 808px !important; 
        height: 85px;
        padding: 17px 0 0 24px;
        margin: 16px auto;
        background: white;
        border-radius: 4px;
        font-weight: normal;
        font-size: 30px !important;
        line-height: 34px;
        text-align: left !important;
        border-bottom: 1px solid #d6b6c1;
	}
	
	.logo br {
	    display: none;
	}
	
	.logo img {
	    float: right;
	    margin: -16px 1px -16px 1px !important;
	    border-radius: 0 2px 2px 0;
	    vertical-align: middle;
	}
	
	/*.logo {
	    color: hsv(336, 49, 27) !important;
	}
	
	.logo img {
        border-radius: 3px;
        border-bottom: 1px solid hsv(338 15 84);
        margin-bottom: 16px;
	}*/
	
	/* Thread */
	
	.DESU_thread, 
	div[id^="thread"] {
        display: block;
	    width: 800px !important;
	    border-bottom: 1px solid #d6b6c1;
	    padding: 8px 16px 16px 16px;
	    margin: 16px auto;
	    background: white;
	    border-radius: 4px;
	    color: #333 !important;
	    overflow: hidden; /* post won't be smaller than thumbnail */
	}
	
	.DESU_oppost {
	    /* display: table-cell; */
	}
	
	.DESU_oppost img.thumb,
	div[id^="thread"] > a > img.thumb {
	    margin-bottom: 8px !important;
	}
	
	.omittedposts {
	    display: block;
	    clear: both;
	    padding: 2px 7px;
	}
    	
	/* Reply */
	
	.reply {
	    background: #eee !important;
	    color: #222 !important;
	    border-radius: 4px;
	    border-top: 1px solid #cacaca !important;
	    border-left: 1px solid #dddddd !important;
	    border-right: 1px solid #dddddd !important;
	    border-bottom: 1px solid #e0e0e0 !important;
	    padding: 2px !important;
	}
	
	div.reply {
	   margin: 4px auto !important;
	}
	
	/*td.reply > blockquote {
	    overflow: auto !important;
  	    width: 765px !important;
	}*/

	div[id^="thread"] > table,
	.DESU_thread > table {
 	    width: 790px !important;
	    clear: both;
	    margin: 8px 0 8px 6px !important;
	    display: table !important;
	}
	
	.highlight {
	    background: #ffdde0 !important;
	    border-radius: 4px;
	    border: 2px solid #ff688e !important;
	    width: 780px !important;
	    color: #7c0012 !important;
	}

    /* Post header */
	
	.postername, 
	div[id^="thread"] label {
        color: #888888 !important;
	}

    .commentpostername, 
    div[id^="thread"] > table label {
	    color: #808080 !important;
	}

    .highlight .commentpostername, 
    table td.highlight label {
	    color: #d6648e !important;
	}

    .filetitle,
	.replytitle {
	   color: #222 !important;
	   font-size: 11pt !important;
	}
	
	.filesize {
	    font-size: 80%;
	    line-height: 1;
	}

	.reflink a {
	   color: #ff72a8 !important;
	}

	.reflink a:hover {
	   color: #ff2176 !important;
	}
	
	.adminname {
	   color: #ff0059 !important;
	}
	
	/* Post content */
	
	img.thumb {
	    border-radius: 3px;
	    margin: 2px !important; margin-right: 16px !important;
	}
	
	span.spoiler {
	    background: #ccc !important;
	    color: #aaa !important;
	    -moz-transition: color 0.2s linear 0s;
	}
	
	span.spoiler:hover {
	    background: #ccc !important;
	    color: #222 !important;
	    -moz-transition: color 0.2s linear 0s;
	}
	
	blockquote.unkfunc {
	    color: #328ff2 !important;
	}

	/* Post form */

    div#DESU_pform,
	div.postarea {
	    width: 800px !important;
	    border-bottom: 1px solid #d6b6c1;
	    padding: 8px 16px;
	    background: white;
	    border-radius: 4px;
   	    margin: 16px auto;
    }
    
    div#DESU_pform > table,
	div.postarea > table {
	   width: auto !important;
	}

    div#DESU_pform input[type="text"],
	div.postarea input[type="text"] {
        width: 460px !important;
    }
    
	.postblock {
	    background: transparent !important;
	    color: #555 !important;
	    font-weight: normal !important;
	    text-align: right;
	    font-size: 10pt !important;
	    padding-right: 4px;
    }
    
    #DESU_txtPanel {
        padding: 4px 0;
    }

    /* Captcha */

    #postform img {
        /*display: inline !important;*/
    }
    
    /* Rules */

    .rules {
   	    font-size: 9pt !important;
    }

    .rules .prohibition,
    font[color="red"] {
        color: #ff0066 !important;
    }
    
    /* Oekaki */
    
    div#DESU_pform input[name^="oek_"] {
        width: auto !important;
    }
    
    div#DESU_pform form[action="/cgi-bin/paint.pl"] {
        margin-left: -16px !important;
        margin-right: -16px !important;
    }
	
	/* Junk */
	
	hr, 
	span.thumbnailmsg,
	form > br, 
	td.doubledash,
	div.theader {
	   display: none;
	}
	  
    /* Dollscript popups */

	div.DESU_pView, div.DESU_pWait,
	td.DESU_pView, td.DESU_pWait,
	#DESU_select,
	#DESU_alertTRepHlp,
	#DESU_alertKNavHlp {
	    border: none !important;
	    background: white !important;
	    border-radius: 4px;
	    padding: 6px 6px !important;
	    box-shadow: 0px 4px 16px rgba(0, 0, 0, 0.35);  
    }
    
    #DESU_alertTRepHlp,
    #DESU_alertKNavHlp {
        margin: 10px !important;
    }
    
    .DESU_alertBtn {
        color: #d30054 !important;
    }
    
    #DESU_select {
        padding: 4px 0 !important;
    }
    
    #DESU_select a:hover {
        background: #ff325f !important;
        color: white !important;
    }

    /* Dollscript quick reply form */

	div.reply #DESU_pform {
 	    width: 780px !important;
 	    border: 0;
 	    padding: 0;
	    margin: 0;
	    background: transparent;
    }
    
    #DESU_qarea {
        padding: 16px;
        border-radius: 4px;
        margin: 10px 0 10px 8px !important;
        width: 780px !important;
        background: #dae9ed !important;
        border-color: #c5d6e8 !important;
    }

    /* Dollscript panel */

    #DESU_panel {
        background: #d30054 !important;
        border-radius: 4px 0 0 0 !important;
    }
    
    #DESU_panelBtns,
    #DESU_panelInfo {
        border-left: 1px solid #f46691 !important;
    }
	
	/* Dollscript config window */

    #DESU_cfgWindow,
    #DESU_contentCfg > .reply {
        border-radius: 6px 6px 0 0 !important;
	    box-shadow: 0px 4px 16px rgba(0, 0, 0, 0.35) !important;
	    background: white !important;
	    margin: 16px !important;
    }
    
    #DESU_cfgHead {
        background: transparent !important;
        font-weight: normal !important;
        color: #444 !important;
    }
    
    #DESU_cfgBar {
        background: transparent !important;
        margin-bottom: 1px;
    }
    
    #DESU_cfgBar > .reply {
        background: transparent !important;
    }
    
    .DESU_cfgTabBack {
        background: transparent !important;
    }

    .DESU_cfgTab {
        background: transparent !important;
	    border-radius: 6px 6px 0 0 !important;
	    border: none !important;
	    border-bottom: 2px solid #ff688e !important;
	    margin-bottom: 1px !important;
        color: #666;
	    padding: 4px 9px 2px 9px !important;
        font-weight: normal !important;
    }
    
    .DESU_cfgTab_sel {
        background: #ffdde0 !important;
	    border-radius: 3px 3px 0 0 !important;
	    border: 2px solid #ff688e !important;
	    border-bottom: none !important;
	    padding: 4px 9px !important;
	    margin-bottom: -2px !important;
	    color: #7c0012 !important;
        font-weight: normal !important;
    }
    
    .DESU_cfgBody {
        background: #ffdde0 !important;
	    border-radius: 0 !important;
	    border: 2px solid #ff688e !important;
	    border-top: none !important;
	    color: #7c0012 !important;
    }
    
    #DESU_cfgBtns {
        border: none !important;
        padding: 3px !important;
    }
    
    #DESU_cfgBtns select,
    #DESU_cfgBtns input {
        margin: 3px !important;
    }

	/* Dollscript misc */

    #DESU_alertBox > .reply {
        border-radius: 6px !important;
	    box-shadow: 0px 4px 16px rgba(0, 0, 0, 0.35) !important;
	    background: white !important;
	    border: none !important;
	    margin: 16px !important;
    }
    
    .DESU_alertMsg {
        padding: 0 6px 0 2px;
    }
	
	.DESU_refMap {
	    padding-left: 3px;
	}
	
    img.DESU_fullImg {
        border: none !important;
        border-radius: 4px;
        box-shadow: 0 2px 4px gray;
    }
    
    .DESU_content .reply {
        margin: 0 !important;
        padding: 0 !important;
        border: 0 !important;
    }

    .DESU_content input[type="checkbox"] {
	    vertical-align: middle;
	}

    div[id^="DESU_hidThr_"] {
        border: none !important;
	    border-bottom: 1px solid #d6b6c1 !important;
	    background: white !important;
	    border-radius: 4px;
	    color: #333 !important;
	    padding: 8px 16px !important;
	    margin: 16px auto !important;
    }
    
    #DESU_select {
        margin: 0 !important;
    }
    
    #DESU_contentFav .reply,
    #DESU_contentHid .reply {
        border: none !important;
	    background: transparent !important;
	    color: #442330 !important;
    }
    
    #DESU_contentFav,
    #DESU_contentHid {
        /*border: 1px solid #888 !important;*/
        border: none !important;
        background: white !important;
        box-shadow: 0px 4px 16px rgba(0, 0, 0, 0.35) !important;
        /*right: 16px !important;
        bottom: 41px !important;
        top: 16px !important;
        max-height: 100% !important;*/
        right: 16px !important;
        bottom: 41px !important;
        max-height: 93% !important;
    }
    
    #DESU_contentFav > table,
    #DESU_contentHid > table {
        background: white !important;
        border: none !important;
    }
    
    .DESU_favPCount span {
        color: #4BA500 !important;
    }
    
    #DESU_txtPanel {
        float: none !important;
    }
    
    /* Pagination */

    table[border="1"] {
        clear: both;
        width: 860px;
        margin: 0 auto !important;
        border-spacing: 16px;
        text-align: center;
    }
    
    table[border="1"] form {
        width: auto;
    }
    
}

@-moz-document 
    url-prefix("http://iichan.hk/n/"),
    url-prefix("http://www.iichan.hk/n/") {

    /* Main page */

    .logo {
        background: transparent !important;
        border: none !important;
        width: auto !important;
        height: auto !important;
        text-align: center !important;
        margin: .8em 0 !important;
        padding: 0 !important;
        color: #442330 !important;
    }
    
    .logo a {
        color: #442330 !important;
    }
        
    .greeting img {
        border-radius: 4px;
        border-bottom: 1px solid #d6b6c1 !important;
    }
    
    .greeting p {
        color: #82475e !important;
        font-weight: normal !important;
    }
    
    div.news {
        background: transparent !important;
        color: #442330 !important;
        font-weight: normal !important;
        font-size: 16pt;
    }
    
    div.news {
        margin: 1em auto;
    }
    
    br {
        display: none;
    }
}

@-moz-document
    url-prefix("http://iichan.hk/n/list"),
    url-prefix("http://www.iichan.hk/n/list") {

    /* Side frame */

    .adminbar {
        width: auto !important;
    }

    .logo:after {
        display: inline-block;
        content: "Теперь клубничный!";
        background: #ff323d !important;
        color: white !important;
        font-size: 10pt !important;
        line-height: 14pt;
        font-weight: bold;
        padding: 0 4px;
        border-radius: 3px;
        white-space: nowrap;
        transform: rotate(-5deg);
        -o-transform: rotate(-5deg);
        -moz-transform: rotate(-5deg);
        -webkit-transform: rotate(-5deg);
        margin-bottom: -7pt;
    }
    
    .category {
        border-bottom: 1px solid #d6b6c1 !important;
	    padding: 4px 4px 8px 4px;
	    margin: 16px auto;
	    background: white !important;
	    border-radius: 4px;
	    color: #333 !important;
    }

    .category .header {
        background: transparent !important;
        font-weight: normal !important;
        font-size: 16pt;
        color: #825869 !important;
    }
    
    .category a:hover {
        background: transparent !important;
    }
}
