/* GKoC's Warrior Cats Forums Nebula Reskin - Chrome and Glass Charcoal */
/* Version 2.0 - "Supernova" */
/* by Good King of Cats */

@-moz-document domain("forums.warriorcats.com") {

/* changes the background */

body {
background: url("http://i.imgur.com/qXu2X.png ") top repeat fixed transparent !important;
background-size: cover !important;
}

/* puts shadows on foreground objects */

#gs_online_now_list,
#gs_menu_y_baseline,
.ubbx_cfrm_cat_tbl,
.ev_tab_table_top,
.ev_com_tbl,
.ev_container_bottom,
.ev_msg_rowcolor1,
.ev_msg_rowcolor2,
.ev_tab_table_bot,
.ev_tab_table_bot,
.ev_body_table,
embed {
box-shadow: 0px 1px 4px #000 !important;
}

/* puts shadows on small non-contained text */

.ev_background_txt,
#ev_copy_txt,
div.ev_ubbx_pages,
div.ev_ubbx_pages b {
text-shadow: 0px 1px 2px #000 !important;
}

/* resets "Online Now:" appendix to fix appearance */

TABLE.ev_container TD.ev_tr_corner,
TABLE.ev_container TD.ev_tr_groupee_corner {
background: inherit; !important;
}

/* makes signature handle better (eg: overlength siggies scroll) */

.ev_ubbx_tpc div.ev_tpc_signature {
margin-top: 1em;
margin-bottom: .7em;
padding-left: 2em;
padding-right: 2em;
padding-bottom: 2em;
overflow-y: auto !important;
height: none;
max-height: 102px;
}

/* changes all light blue objects to a light charcoal color */

BODY.ev_popup,
BODY.ev_iframe,
.ev_msg_rowcolor1,
TABLE.ev_msg_rowcolor1,
.ev_bgcolor_row1,
ABLE.ev_member_table TD,
TH,
TD.ev_body_table_mypace_leftnav_td,
TABLE.ev_ms_content TD.ev_ms_content_title,
TABLE.ev_ms_content TH.ev_ms_content_title,
TABLE.ev_ms_content TD.ev_ms_content_lr,
TABLE.ev_ms_content TH.ev_ms_content_lr,
.ev_ms_header,
.ev_island_news,
.ev_island,
.ev_hopto_normal_hover,
TD.ev_onlinenow_table {
background: #575757 !important;
}

/* changes all darker blue objects to a dark charcoal color */

.ev_hopto_normal,
TABLE.ev_com_tbl TD,
TABLE.ev_com_tbl TH,
TABLE.ev_com_tbl TH,
.ev_menu_logout,
.ev_island_alt,
.ev_island_login,
.ev_island_content,
DIV.ev_ms_nav,
TD.ev_body_table_myspace_content_td,
TABLE.ev_member_table TH,
.ev_bgcolor_row2,
TABLE.ev_body_table,
TABLE.ev_message_alert TD.ev_content,
.ev_msg_rowcolor2,
TABLE.ev_msg_rowcolor2,
TABLE.ev_member_table TD,
TH {
background-color: #424242 !important;
}

/* turns grab-bar text white for easier reading */

TD.ev_grab_bar * {
color: #efefef !important;
}

/* turns grab-bar buttons black again */

TD.ev_grab_bar * INPUT.ev_input_normal {
color: #000000 !important;
}

/* changes border colors of top navigational tabs */

td.eve_tab,
td.eve_tab_hover {
border-bottom-color: #333333 !important;
border-right-color: #333333 !important;
}

/* changes navigational tab text to white for ease of reading */

div.ev_tab a,
div.ev_tab_hover a,
div.ev_tab,
div.ev_tab_hover {
color: #efefef !important;
}

/* further tweaks and recolors tabs */

div.ev_tab {
border-left-color: #666666 !important;
border-top-color: #888888 !important;
border-width: 1px 0 0 1px !important;
}

/* recolors message tabs charcoal */

#gs_tab_smilie,
#gs_tab_color,
#gs_tab_url,
#gs_tab_bold,
#gs_tab_italic,
#gs_tab_strike,
#gs_tab_quote,
#gs_tab_list,
#gs_tab_list,
#gs_tab_email,
#gs_tab_img,
#gs_tab_tag,
#gs_tab_posticon_border {
background-color: #666666 !important;
}

/* recolors header/footer text white */

table.ev_container td *,
table.ev_container_bottom td *,
table.ev_container td a,
table.ev_container td a:visited,
table.ev_container_bottom td a:visited,
table.ev_container td,
table.ev_container_bottom td,
table.ev_container th {
color: #efefef !important;
}

/* recolors header/footer objects charcoal */

table.ubbx_cfrm_cat_tbl,
#gs_tab_posticon_menu,
ABLE.ev_layer_table,
TABLE.ev_help_menu,
TABLE.ev_hopto_table,
TABLE.ev_layer_table TD,
TABLE.ev_help_menu,
.ev_hopto_scroller,
.ev_hopto_emphasized,
DIV.ev_container_bottom,
TABLE.ev_container TD,
TABLE.ev_container_bottom TD,
TABLE.ev_container TH,
INPUT.ev_input,
INPUT.ev_input_normal,
.ev_input_normal,
INPUT.ev_input_small,
.ev_input_small,
DIV.ev_input_div INPUT,
TABLE.ev_message_alert,
TABLE.ev_message_alert TD.ev_grab_bar,
TABLE.ev_post_table TD.ev_post_table_leftcol,
td.eve_tab,
td.eve_tab_hover,
div.ev_tab,
div.ev_tab_hover {
background-color: #333333 !important;
}

/* gives header/footer objects chrome/glass bar texture; adds shadows to text for ease of reading */

#gs_tab_posticon_menu,
ABLE.ev_layer_table,
TABLE.ev_help_menu,
TABLE.ev_hopto_table,
TABLE.ev_layer_table TD,
TABLE.ev_help_menu,
.ev_hopto_scroller,
.ev_hopto_emphasized,
DIV.ev_container_bottom,
TABLE.ev_container TD,
TABLE.ev_container_bottom TD,
TABLE.ev_container TH,
TABLE.ev_message_alert,
TABLE.ev_message_alert TD.ev_grab_bar,
TABLE.ev_post_table TD.ev_post_table_leftcol {
background: url("http://img511.imageshack.us/img511/4307/barbg.png") center repeat !important;
text-shadow: 0px 1px 2px #000 !important;
}

/* removes inner tab container background to make hover-glow work */

div.ev_tab,
div.ev_tab_hover {
background: none !important;
}

/* gives buttons chrome/glass button texture */

td.eve_tab {
background: url("http://img9.imageshack.us/img9/4543/btnbg.png") center !important;
-moz-transition: all 0.2s ease-out;
-webkit-transition: all 0.2s ease-out;
transition: all 0.2s ease-out;
}

/* gives hovered-over buttons a chrome/glass button texture with a dark haze*/

td.eve_tab_hover {
background: url("http://img9.imageshack.us/img9/4543/btnbg.png") rgba(0, 2, 6, 0.75) center !important;
-moz-transition: all 0.2s ease-out;
-webkit-transition: all 0.2s ease-out;
transition: all 0.2s ease-out;
}


/* recolors navigational tab menus charcoal */

table.ev_layer_table td,
table.ev_help_menu td,
table.ev_hopto_table tr td,
input.ev_input,
table.ev_layer_table td,
table.ev_help_menu {
background-color: #2B2B2B !important;
}

/* recolors text of navigational tab menus white */

.ev_layer_table tbody tr td a,
.ev_text_normal,
.ev_layer_table b,
table.ev_layer_table td a:hover,
table.ev_layer_table td a:visited,
.ev_hopto_emphasized a,
.ev_hopto_emphasized_hover a,
.ev_hopto_emphasized_hover a:hover,
.ev_hopto_emphasized_hover a:visited,
table.ev_layer_table td,
table.ev_help_menu td,
table.ev_hopto_table tr td,
table.ev_post_table td.ev_post_table_leftcol *,
table.ev_post_table td.ev_post_table_leftcol {
color: #EFEFEF !important;
}

/* fixes message text so it's its original black color */

textarea#ev_post_field_message,
span textarea.ev_text_normal,
input#gs_msg_insert_bold,
input#ev_post_field_subject {
color: #000000 !important;
}

/* fixes section post-to text so it's its original black color */

#ev_post_field_postto {
color: #000000 !important;
}

/* recolors borders of help menu items */

table.ev_layer_table td,
table.ev_help_menu td,
table.ev_hopto_table tr td {
border-bottom-color: #222222 !important;
border-bottom-width: 1px !important;
}

/* recolors help menu items */

table.ev_layer_table td input {
background-color: #222222 !important;
}

/* recolors help menu header text */

.ev_help_menu_header {
color: #efefef !important;
}

/* recolors help menu headers */

DIV.ev_help_menu_header {
background-color: #2B2B2B !important;
}

/* recolors table borders from favorites menu to black */

TABLE.ev_ms_content,
TABLE.ev_ms_content TH,
TABLE.ev_ms_content TD {
border-color: #000000 !important;
}
}
