css:
@-moz-document domain('dribbble.com') {

/* Dribbble - Dark Background + Sticky Header	*/
/* http://userstyles.org/styles/70445/          */
/* Author: @natebraxton                 	*/

body { background-color: #ECECEC !important; }

#header { position: fixed; top: 0; left: 0; z-index:780; width:100%; }

div.screenshot-meta { width: 220px; background-color: rgba(0, 0, 0, 0.06); padding-right: 10px; border-radius: 6px; border: 1px solid #DDD; }

div.notice { margin-top: 62px; }
div.meta-act div.meta-colors ul.color-chips li a { width: 22px; }
#wrap { background:none; background-color:#666; padding-top: 60px; }

#wrap-inner { background-image:none !important; }

#content { background:#f0f0f0 url(http://www.dribble.com/images/noise-f1.gif) repeat -70% 0 !important; padding:30px; border-radius: 12px; }

#bucket-add ol.buckets, #project-add ol.projects, #list-add ol.lists { margin-top:20px; }

#secondary { margin-top:70px; }

#footer {background-color: #ECECEC;}

}
