css:
@-moz-document domain("youtube.com") {
#masthead {
    position: static !important;
    padding-top: 25px !important;
    height: 50px !important;
}

#logo {
    position: absolute !important;
    display: inline-block !important;
    background: url('http://i.imgur.com/zGjPO.png') no-repeat !important;
    width: 80px !important;
    height: 100px !important;
    top: 2px !important;
    z-index: 100 !important;
}

#masthead-search-bar {
    margin-left: 60px !important;
}

#error-page-content #masthead #logo-container {
display:none;
}

body{
background-color: black!important;
background: url('http://i.imgur.com/pn9jN.jpg') #000 fixed no-repeat 50% 0% !important;
background-size:130% 100% !important;
}

#masthead-user-expander .yt-uix-expander-head, #masthead-gaia-user-wrapper, #masthead-gaia-photo-wrapper {
border: 0px solid #EAEAEA!important;
}

#masthead-expanded-container {
background: rgba(255,255,255,0.85)!important;
border-radius: 5px!important;
}

#watch-info {
background: rgba(255,255,255,0.9)!important;
border: 5px solid rgba(255,255,255,0.1)!important;
border-radius: 5px 5px 5px 5px!important;
}

#watch-discussion {
background: rgba(255,255,255,0.9)!important;
border: 5px solid rgba(255,255,255,0.1)!important;
border-radius: 5px 5px 5px 5px!important;
}

.watch-sidebar-body .video-list {
background: rgba(255,255,255,0.9)!important;
border: 5px solid rgba(255,255,255,0.25)!important;
border-radius: 5px 5px 5px 5px!important;
}

#browse-side-column, #browse-main-column {
background: rgba(255,255,255,0.9)!important;
}

.browse-content {
background: #fff !important;
opacity: 0.95;
}

#subscription-confirm {
background: #fff !important;
opacity: 0.95;
padding: 5px;
}
.charts {
background: #fff !important;
opacity: 0.95;
}

.browse-stage {
opacity: 0.95;
}

.yt-horizontal-rule {
display: none!important;
}

#footer ul {
text-shadow: none!important;
}

#footer-links-primary a, #footer-links-secondary a, #footer .pickers a, #picker-loading {
color: #111!important;
}

.yt-uix-button-text, body .yt-uix-button-text[disabled] {
background-image: -webkit-linear-gradient(top,#fff 0,#e0e0e0 100%) !important;
background-image: -moz-linear-gradient(top,#fff 0,#e0e0e0 100%) !important;
}

#masthead-nav a, #masthead-user a {
font-weight: bold!important;
color: #fff !important;
}

#masthead-nav a:hover, #masthead-user a:hover {
color: #fff !important;
}

#masthead-container, #branded-page-body-container {
background: transparent !important;
}

#watch-headline {
border-top-right-radius: 5px !important;
}

#watch-headline, #watch-main {
background: #fff !important;
opacity:0.95!important;
}

#watch-player {
padding-left: 5px;
}

#watch-video {
background: #fff !important;
}

#watch-actions-right {
border-radius: 3px!important;
height: 32px!important;
background-image: -moz-linear-gradient(top,#F0F0F0 0,#E6E6E6 100%)!important;
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#F0F0F0),color-stop(100%,#E6E6E6))!important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.25),inset 0 0 3px white!important;
text-shadow: 0 1px 0 white;!important;
border-color: #CCC!important;
}

#footer {
border-radius: 3px!important;
height: 32px!important;
background-image: -moz-linear-gradient(top,#F0F0F0 0,#E6E6E6 100%)!important;
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#F0F0F0),color-stop(100%,#E6E6E6))!important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.25),inset 0 0 3px white!important;
border-left: solid 15px #eee!important;
margin-top: 15px!important;
width: 955px!important;
height: 0px!important;
opacity:0.95!important;
}

.video-thumb {
background: #eee!important;
}

.guide-layout-container {
background: rgba(255,255,255,0.7)!important;
border-top-right-radius: 5px !important;
}

div.feed-item-thumb, div.video-thumb {
position: relative !important;
left: 50px;
z-index: 200;
height: 200px important;
}

.feed-item-actions-line {
position: relative;
right:88px;
}

.feed-page > ul {
position: relative !important; 
width: 770px !important;
margin-left: 10px!important;
}

.feed-page > ul > li {
width: 770px!important; 
float: left !important;
}

.feed-header{
width: 970px !important;
position: relative !important;
right: 200px !important;
border-top-left-radius: 5px !important;
}

.feed-container{
width: 800px!important;
position: relative!important;
right: 15px!important;
border-bottom: solid 0px white !important;
border-radius: 0px 0px 45px 30px !important;
background: transparent!important;
}

.feed-container .feed-item-container{
height: 175px !important;
margin-left: 10px!important;
margin-bottom: 5px !important;
margin-top: 5px !important;
border: 2px solid #888 !important;
border-radius: 3px !important;
background: rgba(255,255,255,1)!important;
}

.feed-container .feed-item-outer {
padding: 0px !important;
margin-left: 15px !important;
}

.feed-container .feed-load-more-container {
margin-left: 20px !important;
margin-right: 20px !important;
clear: left
}

.feed-container .feed-item-main .feed-item-thumb {
-webkit-box-shadow:#555 5px 5px 20px!important;
-moz-box-shadow:#555 5px 5px 20px!important;
margin-top: 10px !important;
}

.feed-container .feed-item-main {
border: solid 0px #999!important;
border-radius: 1px !important;
padding: 20px!important;
margin-left: -17px!important;
position: relative;
bottom: 47px;
}

.feed-container .feed-item-main .feed-item-content {
margin-left: auto !important;
margin-right: auto !important;
}

.feed-container .feed-item-main .feed-item-time {
top: 11px !important;
right: 10px !important;
color: #222 !important;
}

.feed-container .feed-item-main h4 {
position: relative;
left: 70px;
top: 10px;
font-size: 13px !important;
width: 550px !important;
height: 15px !important;
white-space: nowrap !important;
text-overflow: ellipsis !important;
}

.feed-container .feed-item-main .feed-item-actions-line {
margin-left: 135px !important;
height: 0px !important;
}

.feed-container .feed-item-main .description {
height: 300px !important;
width: 450px !important;
word-wrap: break-word !important;
text-overflow: ellipsis !important;
color: #888 !important;
}

.feed-container .feed-item-content p.metadata {
font-size: 12px !important;
margin-top: 6px !important;
margin-left: 135px !important;
}

.feed-container .feed-item-main .metadata, .playlist-titles {
height: 400px !important;
position: relative;
left: 70px;
top: 10px;
}

#feed-social-connect .feed-container {
padding-left: 10px !important;
}

.social-service-buttons {
margin-right: 20px !important;
}

.feed-load-more {
width: 950px!important;
}

#video-sidebar, #video-sidebar ul, .guide-background, #feed-background, .feed-item .thumbs-list, .horizontal-rule, #google_companion_ad_div, #instream_google_companion_ad_div, .ad-div.mastad, #search-pva-content, #search-base-div .ppv-promoted-videos, #search-base-div .pyv-promoted-videos, #watch-branded-related {
display: none!important;
}

#content {
width:970px !important;
}

#page.watch-branded #content, #page.watch-branded #watch-sidebar, #page.watch-branded #watch-main-container {
background: transparent !important;
}

#watch-headline h1 {
color: #333 !important;
}

#search-main, #search-refinements, #search-refinements .single-line-lego-list {
background: rgba(255,255,255,0.9)!important;
border: 5px solid rgba(255,255,255,0.1)!important;
border-radius: 5px 5px 5px 5px!important;
width: 100%!important;
}

#yt-admin {
background: #fff !important;
opacity: 0.95 !important;
padding-right: 0px;
}

.ytg-wide {
background: #fff !important;
opacity: 0.95 !important;
}

#yt-microsite-header, #yt-microsite-features {
background: #fff !important;
opacity: 0.9;
margin: 5px auto;
}

#yt-admin-content {
padding-left:1px;
}

li.menu-item {
text-shadow: 0 1px 1px rgba(255, 255, 255, .5) !important;
}

#error-page-content {
background: #fff;
border-radius: 5px 5px 5px 5px!important;
}

#ticker-inner .ytg-wide {
background-image: -webkit-linear-gradient(top,#849FC2 0,#6683B3 100%) !important;
background-image: linear-gradient(to bottom,#849FC2 0,#6683B3 100%) !important;
}

#baseDiv {
width: 990px !important;
}
}

@-moz-document url-prefix("http://www.youtube.com/html5"), url-prefix("http://www.youtube.com/feather_beta") {
#masthead, #footer {
width: 990px !important;
}

#content, #baseDiv {
padding:6px;
background: #fff;
opacity: 0.95;
border: 5px solid rgba(255,255,255,0.1)!important;
border-radius: 5px 5px 5px 5px!important;
}
}

@-moz-document url-prefix("http://www.youtube.com/user") {
#branded-page-body-container {
position: relative;
bottom: 10px;
}

.branded-banner-image {
display: none;
}

.upper-left-section  h1 {
color: #000 !important;
}

.report-image-container {
background: transparent !important;
margin-bottom: 0px;
position: relative;
bottom:15px;
}
}
